import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Paper,
  CircularProgress,
  Card,
  CardContent,
  Chip,
  Button,
} from "@mui/material";

const formatDate = (date) =>
  new Date(date).toLocaleDateString("id-ID", {
    day: "2-digit",
    month: "short",
    year: "numeric",
  });

const getWeekStart = (date) => {
  const d = new Date(date);
  const day = d.getDay();
  const diff = d.getDate() - day + (day === 0 ? -6 : 1);
  return new Date(d.setDate(diff));
};

const DeploymentBoardPage = () => {
  const [deploymentRequests, setDeploymentRequests] = useState([]);
  const [deploymentSupports, setDeploymentSupports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentWeekIndex, setCurrentWeekIndex] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);

      // Ambil data 2 bulan ke depan agar semua minggu tercakup
      const startDate = new Date();
      const endDate = new Date();
      endDate.setMonth(endDate.getMonth() + 2);

      try {
        const [reqRes, supRes] = await Promise.all([
          api.get("/deployment-requests", {
            params: {
              startDate: startDate.toISOString().split("T")[0],
              endDate: endDate.toISOString().split("T")[0],
            },
          }),
          api.get("/deployment-supports", {
            params: {
              startDate: startDate.toISOString().split("T")[0],
              endDate: endDate.toISOString().split("T")[0],
            },
          }),
        ]);

        setDeploymentRequests(reqRes.data?.data || []);
        setDeploymentSupports(supRes.data?.data || []);
      } catch (err) {
        console.error("❌ Gagal mengambil data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
        <CircularProgress />
      </Box>
    );
  }

  // Gabungkan request & support
  const allEvents = [
    ...deploymentRequests.map((r) => ({ ...r, type: "request", date: r.implementDate })),
    ...deploymentSupports.map((s) => ({ ...s, type: "support", date: s.implementDate })),
  ].filter((e) => e.date); // pastikan tanggal ada

  // Kelompokkan event berdasarkan minggu
  const eventsByWeek = {};
  allEvents.forEach((event) => {
    const weekStart = getWeekStart(event.date).toISOString().split("T")[0];
    if (!eventsByWeek[weekStart]) eventsByWeek[weekStart] = [];
    eventsByWeek[weekStart].push(event);
  });

  // Urutkan minggu
  const weekKeys = Object.keys(eventsByWeek).sort((a, b) => new Date(a) - new Date(b));
  const totalWeeks = weekKeys.length;

  // Tangani kalau belum ada data
  if (totalWeeks === 0) {
    return (
      <Typography sx={{ textAlign: "center", mt: 5 }}>Tidak ada data deployment ditemukan.</Typography>
    );
  }

  // Pastikan index valid
  const activeWeek = weekKeys[Math.min(currentWeekIndex, totalWeeks - 1)];
  const weekStartDate = new Date(activeWeek);
  const weekEndDate = new Date(weekStartDate);
  weekEndDate.setDate(weekEndDate.getDate() + 6);

  // Warna risk
  const riskColor = (event) => {
    if (event.type === "support") return "#90caf9";
    switch (event.riskImpact) {
      case "Low":
        return "#A5D6A7";
      case "Medium":
        return "#FFF59D";
      case "High":
        return "#EF9A9A";
      case "Major Release":
        return "#3a3a3a";
      default:
        return "#E0E0E0";
    }
  };

  // Fungsi render kolom hari
  const renderDayColumn = (day, offset) => {
    const currentDate = new Date(weekStartDate);
    currentDate.setDate(currentDate.getDate() + offset);

    const events = (eventsByWeek[activeWeek] || []).filter(
      (e) => new Date(e.date).toDateString() === currentDate.toDateString()
    );
    const requests = events.filter((e) => e.type === "request");
    const supports = events.filter((e) => e.type === "support");

    return (
      <Paper key={day} sx={{ p: 2, textAlign: "center" }}>
        <Typography variant="subtitle1" fontWeight="bold" sx={{ mb: 1 }}>
          {day} ({formatDate(currentDate)})
        </Typography>

        <Box sx={{ display: "flex", justifyContent: "space-between", alignItems: "stretch", gap: 1 }}>
          {/* Request */}
          <Box sx={{ flex: 1 }}>
            {requests.map((event) => (
              <Card
                key={event.id}
                sx={{
                  backgroundColor: riskColor(event),
                  mb: 1,
                }}
              >
                <CardContent
                  sx={{
                    px: 1,
                    py: 0,
                    "&:last-child": { pb: 0 },
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    textAlign: "center",
                    minHeight: 48,
                  }}
                >
                  <Typography variant="body2" fontWeight="bold" noWrap>
                    {event.title}
                  </Typography>
                </CardContent>
              </Card>
            ))}
          </Box>

          {/* Garis pemisah */}
          <Box sx={{ width: "1px", backgroundColor: "#ccc" }} />

          {/* Support */}
          <Box sx={{ flex: 1 }}>
            {supports.map((event) => (
              <Card
                key={event.id}
                sx={{
                  backgroundColor: riskColor(event),
                  mb: 1,
                }}
              >
                <CardContent
                  sx={{
                    px: 1,
                    py: 0,
                    "&:last-child": { pb: 0 },
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    textAlign: "center",
                    minHeight: 48,
                  }}
                >
                  <Typography variant="body2" fontWeight="bold" noWrap>
                    {event.title}
                  </Typography>
                </CardContent>
              </Card>
            ))}
          </Box>
        </Box>
      </Paper>
    );
  };

  return (
    <>
      <Typography
        variant="h5"
        fontWeight="bold"
        sx={{ color: "#1976d2", mb: 2, textAlign: "center" }}
      >
        Deployment Board (Per Minggu)
      </Typography>

      {/* Pagination Controls */}
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", mb: 3, gap: 2 }}>
        <Button
          variant="outlined"
          disabled={currentWeekIndex === 0}
          onClick={() => setCurrentWeekIndex((prev) => Math.max(prev - 1, 0))}
        >
          ← Minggu Sebelumnya
        </Button>

        <Typography variant="subtitle1" fontWeight="bold">
          {formatDate(weekStartDate)} - {formatDate(weekEndDate)}
        </Typography>

        <Button
          variant="outlined"
          disabled={currentWeekIndex === totalWeeks - 1}
          onClick={() => setCurrentWeekIndex((prev) => Math.min(prev + 1, totalWeeks - 1))}
        >
          Minggu Berikutnya →
        </Button>
      </Box>

      {/* Grid Minggu Aktif */}
      <Box sx={{ display: "grid", gridTemplateColumns: "repeat(4, 1fr)", gap: 2, mb: 2 }}>
        {["Senin", "Selasa", "Rabu", "Kamis"].map((day, idx) => renderDayColumn(day, idx))}
      </Box>

      <Box sx={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: 2 }}>
        {["Jumat", "Sabtu", "Minggu"].map((day, idx) => renderDayColumn(day, idx + 4))}
      </Box>
    </>
  );
};

export default DeploymentBoardPage;
