import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import TaskListPage from "./pages/TaskListPage"; // Import TaskListPage
import CreateTaskPage from "./pages/CreateTaskPage";
import LoginPage from "./pages/LoginPage";
import MainLayout from "./layout/MainLayout"; // Import MainLayout

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

function AppContent() {
  return (
    <Routes>
      <Route path="/login" element={<LoginPage />} />
      <Route path="/tasks" element={<MainLayout><TaskListPage /></MainLayout>} /> {/* Pastikan TaskListPage ada */}
      <Route path="/create" element={<MainLayout><CreateTaskPage /></MainLayout>} />
      <Route path="/" element={<MainLayout><TaskListPage /></MainLayout>} /> {/* Halaman default */}
    </Routes>
  );
}

export default App;
