import React from "react";
import { Box, Typography, TextField, Button, Paper } from "@mui/material";

const CreateTaskPage = () => {
  return (
    <Box
      sx={{
        display: "flex",
      }}
    >
      {/* Konten utama */}
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          marginLeft: "220px", // ðŸ’¡ beri jarak sesuai lebar drawer
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
          backgroundColor: "transparent", // biar background tetap bening
        }}
      >
        <Paper
          elevation={3}
          sx={{
            p: 4,
            width: 400,
          }}
        >
          <Typography variant="h6" sx={{ mb: 2, textAlign: "center", color: "#1976d2" }}>
            Tambah Task Baru
          </Typography>
          <TextField label="Judul Task" fullWidth sx={{ mb: 2 }} />
          <TextField label="Bentuk Support" fullWidth sx={{ mb: 2 }} />
          <TextField label="Aplikasi" fullWidth sx={{ mb: 2 }} />
          <TextField label="Deskripsi" multiline rows={3} fullWidth sx={{ mb: 2 }} />
          <TextField label="PIC SQI" fullWidth sx={{ mb: 2 }} />
          <Button variant="contained" fullWidth>
            Simpan Task
          </Button>
        </Paper>
      </Box>
    </Box>
  );
};

export default CreateTaskPage;
