import React, { useState } from "react";
import { TextField, Button, Typography, Box, Paper } from "@mui/material";
import { useNavigate } from "react-router-dom";
import api from "../services/api";

const LoginPage = () => {
  const [form, setForm] = useState({
    username: "",
    password: "",
  });
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Handle input change
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await api.post("/auth/login", form);
      const { token, role, username } = response.data;

      localStorage.setItem("token", token);
      localStorage.setItem("role", role);
      localStorage.setItem("username", username);

      if (role === "sqi") navigate("/tasks");
      else if (role === "developer") navigate("/create");
      else setError("Role tidak dikenali.");
    } catch (err) {
      console.error("Login error:", err);
      if (err.response?.status === 401) setError("Username atau password salah.");
      else setError("Terjadi kesalahan saat login.");
    }
  };

  return (
    <Box
      sx={{
        height: "100vh",
        width: "100vw",
        background: "linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Paper
        elevation={6}
        sx={{
          padding: 4,
          borderRadius: 3,
          width: "90%",
          maxWidth: 400,
          textAlign: "center",
          backgroundColor: "#fff",
        }}
      >
        <Typography variant="h5" sx={{ fontWeight: "bold", color: "#2575fc", mb: 2 }}>
          Login
        </Typography>

        <Box component="form" onSubmit={handleSubmit}>
          <TextField
            fullWidth
            label="Username"
            name="username"
            value={form.username}
            onChange={handleChange}
            margin="normal"
            required
          />
          <TextField
            fullWidth
            label="Password"
            name="password"
            type="password"
            value={form.password}
            onChange={handleChange}
            margin="normal"
            required
          />
          {error && (
            <Typography variant="body2" color="error" sx={{ mt: 1 }}>
              {error}
            </Typography>
          )}
          <Button
            type="submit"
            variant="contained"
            fullWidth
            sx={{
              mt: 3,
              py: 1.2,
              backgroundColor: "#2575fc",
              "&:hover": { backgroundColor: "#1e63d6" },
              fontWeight: "bold",
            }}
          >
            LOGIN
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default LoginPage;
