import { useEffect, useState } from "react";
import api from "../services/api";

function HomePage() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await api.get("/tasks"); // ✅ otomatis jadi http://localhost:4000/api/tasks
        setTasks(res.data);
      } catch (err) {
        console.error("Error fetching tasks:", err);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h2>Task List</h2>
      {tasks.length === 0 ? <p>No tasks yet</p> : tasks.map((t) => <p key={t.id}>{t.title}</p>)}
    </div>
  );
}

export default HomePage;
