import React, { useState, useEffect } from "react";
import {
  Box,
  Typography,
  TextField,
  Button,
  MenuItem,
  Paper,
  Snackbar,
  Alert,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import UploadFileIcon from "@mui/icons-material/UploadFile";
import api from "../services/api";

// üîπ Import komponen date picker dari MUI X
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import dayjs from "dayjs";

const CreateDeploymentRequestPage = () => {
  const [form, setForm] = useState({
    releaseId: "",
    title: "",
    implementDate: "",
    applicationId: "",
    riskImpact: "Low",
    attachment: null,
  });

  const [applications, setApplications] = useState([]);
  const [freezeDates, setFreezeDates] = useState([]); // daftar tanggal freeze
  const [alertOpen, setAlertOpen] = useState(false);
  const [alertType, setAlertType] = useState("success");
  const [alertMessage, setAlertMessage] = useState("");

  const navigate = useNavigate();

  // üîπ Ambil data aplikasi dan freeze date dari API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");

        const [appRes, freezeRes] = await Promise.all([
          api.get("/applications", {
            headers: { Authorization: `Bearer ${token}` },
          }),
          api.get("/freeze-dates", {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);

        setApplications(appRes.data?.data || []);
        setFreezeDates(
          (freezeRes.data?.data || []).map((f) =>
            new Date(f.date).toISOString().split("T")[0]
          )
        );
      } catch (err) {
        console.error("‚ùå Gagal mengambil data:", err);
      }
    };

    fetchData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleFileChange = (e) => {
    setForm({ ...form, attachment: e.target.files[0] });
  };

  const handleAlertClose = (_, reason) => {
    if (reason === "clickaway") return;
    setAlertOpen(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("releaseId", form.releaseId);
    formData.append("title", form.title);
    formData.append("implementDate", form.implementDate);
    formData.append("applicationId", form.applicationId);
    formData.append("riskImpact", form.riskImpact);
    if (form.attachment) formData.append("attachment", form.attachment);

    try {
      await api.post("/deployment-requests", formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
          "Content-Type": "multipart/form-data",
        },
      });

      setAlertType("success");
      setAlertMessage("Deployment request created successfully!");
      setAlertOpen(true);
      setTimeout(() => navigate("/deployment-board"), 2000);
    } catch (err) {
      console.error("‚ùå Failed to create deployment request:", err);
      setAlertType("error");
      setAlertMessage("Failed to save deployment request.");
      setAlertOpen(true);
    }
  };

  return (
    <Box sx={{ display: "flex" }}>
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 0,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
          backgroundColor: "transparent",
        }}
      >
        <Paper elevation={3} sx={{ p: 4, width: 400, borderRadius: 2 }}>
          <Typography
            variant="h6"
            sx={{
              mb: 2,
              textAlign: "center",
              color: "#1976d2",
              fontWeight: "bold",
            }}
          >
            Request Deployment
          </Typography>

          <Box component="form" onSubmit={handleSubmit}>
            <TextField
              label="Release ID"
              name="releaseId"
              value={form.releaseId}
              onChange={handleChange}
              fullWidth
              sx={{ mb: 2 }}
              required
            />

            <TextField
              label="Title"
              name="title"
              value={form.title}
              onChange={handleChange}
              fullWidth
              sx={{ mb: 2 }}
              required
            />

            {/* üìÖ DatePicker dengan disable freeze date */}
            <LocalizationProvider dateAdapter={AdapterDayjs}>
              <DatePicker
                label="Select Date"
                value={form.implementDate ? dayjs(form.implementDate) : null}
                onChange={(newValue) => {
                  const selectedDate = newValue
                    ? newValue.format("YYYY-MM-DD")
                    : "";
                  if (freezeDates.includes(selectedDate)) {
                    setAlertType("error");
                    setAlertMessage(
                      "Tanggal ini termasuk freeze date dan tidak dapat dipilih!"
                    );
                    setAlertOpen(true);
                    return;
                  }
                  setForm({ ...form, implementDate: selectedDate });
                }}
                shouldDisableDate={(date) =>
                  freezeDates.includes(date.format("YYYY-MM-DD"))
                }
                disablePast
                slotProps={{
                  textField: {
                    fullWidth: true,
                    sx: { mb: 2 },
                    required: true,
                  },
                }}
              />
            </LocalizationProvider>

            <TextField
              select
              label="Application"
              name="applicationId"
              value={form.applicationId}
              onChange={handleChange}
              fullWidth
              sx={{ mb: 2 }}
              required
            >
              {applications.map((app) => (
                <MenuItem key={app.id} value={app.id}>
                  {app.name}
                </MenuItem>
              ))}
            </TextField>

            <TextField
              select
              label="Risk Impact"
              name="riskImpact"
              value={form.riskImpact}
              onChange={handleChange}
              fullWidth
              sx={{ mb: 2 }}
            >
              <MenuItem value="Low">Low</MenuItem>
              <MenuItem value="Medium">Medium</MenuItem>
              <MenuItem value="High">High</MenuItem>
              <MenuItem value="Major Release">Major Release</MenuItem>
            </TextField>

            <Button
              variant="outlined"
              component="label"
              startIcon={<UploadFileIcon />}
              fullWidth
              sx={{ mb: 2, textTransform: "none" }}
            >
              {form.attachment ? "Change File" : "Select Attachment File"}
              <input type="file" hidden name="attachment" onChange={handleFileChange} />
            </Button>

            {form.attachment && (
              <Typography
                variant="body2"
                sx={{ mb: 2, color: "text.secondary", fontStyle: "italic" }}
              >
                üìÑ {form.attachment.name}
              </Typography>
            )}

            <Button variant="contained" fullWidth type="submit">
              Save
            </Button>
          </Box>
        </Paper>
      </Box>

      <Snackbar
        open={alertOpen}
        autoHideDuration={3000}
        onClose={handleAlertClose}
        anchorOrigin={{ vertical: "top", horizontal: "center" }}
      >
        <Alert
          onClose={handleAlertClose}
          severity={alertType}
          variant="filled"
          sx={{ width: "100%" }}
        >
          {alertMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default CreateDeploymentRequestPage;
