import React from "react";
import { Container, Typography, Button, Box } from "@mui/material";
import { useNavigate } from "react-router-dom";
import MainLayout from "../layout/MainLayout";
import TaskList from "../components/TaskList"; // ✅ Import komponen tabel

const TaskListPage = () => {
  const navigate = useNavigate();

  return (
    <MainLayout>
      <Container
        sx={{
          mt: 5,
          padding: 3,
          background: "linear-gradient(135deg, #6a11cb 0%, #2575fc 100%)",
          borderRadius: 2,
        }}
      >
        <Typography
          variant="h4"
          gutterBottom
          sx={{
            fontWeight: "bold",
            color: "#fff",
            textAlign: "center",
            mb: 4,
          }}
        >
          Daftar Task Support
        </Typography>

        {/* Button Add Task */}
        <Box sx={{ display: "flex", justifyContent: "flex-end", mb: 3 }}>
          <Button
            variant="contained"
            color="primary"
            onClick={() => navigate("/create")}
            sx={{
              textTransform: "none",
              fontWeight: "bold",
              fontSize: "16px",
              padding: "10px 20px",
              "&:hover": {
                backgroundColor: "#1e63d6",
              },
            }}
          >
            Tambah Task Baru
          </Button>
        </Box>

        {/* Daftar Task */}
        <Box
          sx={{
            backgroundColor: "#fff",
            borderRadius: 3,
            boxShadow: 3,
            padding: 3,
            overflowX: "auto", // Untuk tampilan responsif saat data banyak
          }}
        >
          {/* Komponen daftar task */}
          <TaskList />
        </Box>
      </Container>
    </MainLayout>
  );
};

export default TaskListPage;
