import React, { useState, useEffect } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  TextField,
  Button,
  CircularProgress,
  IconButton,
  Stack,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import SaveIcon from "@mui/icons-material/Save";
import CloseIcon from "@mui/icons-material/Close";

const KnowledgeCenter = () => {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  // form tambah data
  const [problem, setProblem] = useState("");
  const [solution, setSolution] = useState("");

  // edit data
  const [editId, setEditId] = useState(null);
  const [editProblem, setEditProblem] = useState("");
  const [editSolution, setEditSolution] = useState("");

  // dialog delete
  const [deleteId, setDeleteId] = useState(null);
  const [openConfirm, setOpenConfirm] = useState(false);

  // ambil data dari API
  const fetchData = async (searchTerm = "") => {
    setLoading(true);
    try {
      const res = await api.get(`/knowledge-center?search=${searchTerm}`);
      setData(res.data.data || []);
    } catch (err) {
      console.error("‚ùå Gagal mengambil data:", err);
    } finally {
      setLoading(false);
    }
  };

  // live search debounce
  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      fetchData(search);
    }, 500);
    return () => clearTimeout(delayDebounce);
  }, [search]);

  // tambah entry baru
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!problem || !solution) return;
    try {
      await api.post("/knowledge-center", { problem, solution });
      setProblem("");
      setSolution("");
      fetchData();
    } catch (err) {
      console.error("‚ùå Gagal menambah data:", err);
    }
  };

  // masuk ke mode edit
  const handleEdit = (item) => {
    setEditId(item.id);
    setEditProblem(item.problem);
    setEditSolution(item.solution);
  };

  // batal edit
  const handleCancelEdit = () => {
    setEditId(null);
    setEditProblem("");
    setEditSolution("");
  };

  // simpan hasil edit
  const handleSaveEdit = async (id) => {
    try {
      await api.put(`/knowledge-center/${id}`, {
        problem: editProblem,
        solution: editSolution,
      });
      setEditId(null);
      fetchData();
    } catch (err) {
      console.error("‚ùå Gagal memperbarui data:", err);
    }
  };

  // konfirmasi hapus
  const confirmDelete = (id) => {
    setDeleteId(id);
    setOpenConfirm(true);
  };

  // hapus data
  const handleDelete = async () => {
    try {
      await api.delete(`/knowledge-center/${deleteId}`);
      setOpenConfirm(false);
      fetchData();
    } catch (err) {
      console.error("‚ùå Gagal menghapus data:", err);
    }
  };

  return (
    <Box p={4}>
      <Typography variant="h5" fontWeight="bold" mb={3}>
        üìò Knowledge Center
      </Typography>

      {/* üîç Live Search */}
      <TextField
        label="Cari Problem..."
        variant="outlined"
        fullWidth
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        sx={{ mb: 3 }}
      />

      {/* ‚ûï Form Tambah */}
      <Box
        component="form"
        onSubmit={handleSubmit}
        sx={{ mb: 4, display: "flex", gap: 2, flexWrap: "wrap" }}
      >
        <TextField
          label="Problem"
          variant="outlined"
          fullWidth
          value={problem}
          onChange={(e) => setProblem(e.target.value)}
        />
        <TextField
          label="Solution"
          variant="outlined"
          fullWidth
          value={solution}
          onChange={(e) => setSolution(e.target.value)}
        />
        <Button
          type="submit"
          variant="contained"
          sx={{ minWidth: "120px", backgroundColor: "#1976d2" }}
        >
          Simpan
        </Button>
      </Box>

      {/* üîÑ Loading Indicator */}
      {loading ? (
        <CircularProgress />
      ) : data.length === 0 ? (
        <Typography color="text.secondary">Belum ada data.</Typography>
      ) : (
        data.map((item) => (
          <Accordion key={item.id} sx={{ mb: 2 }}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}>
              <Stack
                direction="row"
                justifyContent="space-between"
                alignItems="center"
                sx={{ width: "100%" }}
              >
                {editId === item.id ? (
                  <TextField
                    fullWidth
                    variant="standard"
                    value={editProblem}
                    onChange={(e) => setEditProblem(e.target.value)}
                  />
                ) : (
                  <Typography fontWeight="bold">{item.problem}</Typography>
                )}

                {editId === item.id ? (
                  <Stack direction="row" spacing={1}>
                    <IconButton onClick={() => handleSaveEdit(item.id)}>
                      <SaveIcon color="success" />
                    </IconButton>
                    <IconButton onClick={handleCancelEdit}>
                      <CloseIcon color="error" />
                    </IconButton>
                  </Stack>
                ) : (
                  <Stack direction="row" spacing={1}>
                    <IconButton onClick={() => handleEdit(item)}>
                      <EditIcon color="primary" />
                    </IconButton>
                    <IconButton onClick={() => confirmDelete(item.id)}>
                      <DeleteIcon color="error" />
                    </IconButton>
                  </Stack>
                )}
              </Stack>
            </AccordionSummary>

            <AccordionDetails>
              {editId === item.id ? (
                <TextField
                  fullWidth
                  multiline
                  minRows={2}
                  variant="standard"
                  value={editSolution}
                  onChange={(e) => setEditSolution(e.target.value)}
                />
              ) : (
                <Typography>{item.solution}</Typography>
              )}
            </AccordionDetails>
          </Accordion>
        ))
      )}

      {/* ‚ö†Ô∏è Dialog Konfirmasi Hapus */}
      <Dialog open={openConfirm} onClose={() => setOpenConfirm(false)}>
        <DialogTitle>Hapus Data</DialogTitle>
        <DialogContent>
          <Typography>Apakah Anda yakin ingin menghapus data ini?</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenConfirm(false)}>Batal</Button>
          <Button onClick={handleDelete} color="error" variant="contained">
            Hapus
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default KnowledgeCenter;
