import React, { useState, useEffect } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  TextField,
  Stack,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  CircularProgress,
  Pagination,
  InputAdornment,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import SearchIcon from "@mui/icons-material/Search";
import AddCircleOutlineIcon from "@mui/icons-material/AddCircleOutline";

const KnowledgeCenter = () => {
  const [data, setData] = useState([]);
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [open, setOpen] = useState(false);
  const [problem, setProblem] = useState("");
  const [solution, setSolution] = useState("");
  const [appForNew, setAppForNew] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [page, setPage] = useState(1);
  const itemsPerPage = 5;

  useEffect(() => {
    fetchData();
    fetchApplications();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const res = await api.get("/knowledge-center");
      setData(res.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false);
    }
  };

  const fetchApplications = async () => {
    try {
      const res = await api.get("/applications");
      setApplications(res.data);
    } catch (error) {
      console.error("Error fetching applications:", error);
    }
  };

  const handleOpen = () => setOpen(true);
  const handleClose = () => {
    setOpen(false);
    setProblem("");
    setSolution("");
    setAppForNew("");
  };

  const handleSubmit = async () => {
    try {
      await api.post("/knowledge-center", {
        problem,
        solution,
        applicationId: appForNew, // kirim ID sesuai backend
      });
      handleClose();
      fetchData(); // reload otomatis
    } catch (error) {
      console.error("Error:", error);
      alert("Gagal menambah data. Pastikan backend menerima field 'applicationId'.");
    }
  };

  // Ambil nama aplikasi berdasarkan ID
  const getAppNameById = (id) => {
    const app = applications.find((a) => a.id === id);
    return app ? app.name : "—";
  };

  // Filter data berdasarkan search term
  const filteredData = data.filter((item) => {
    const appName = getAppNameById(item.applicationId).toLowerCase();
    return (
      appName.includes(searchTerm.toLowerCase()) ||
      item.problem.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  const paginatedData = filteredData.slice(
    (page - 1) * itemsPerPage,
    page * itemsPerPage
  );

  return (
    <Box sx={{ p: 3 }}>
      <Stack
        direction="row"
        justifyContent="space-between"
        alignItems="center"
        mb={2}
      >
        <TextField
          size="small"
          placeholder="Search by application or problem"
          value={searchTerm}
          onChange={(e) => {
            setSearchTerm(e.target.value);
            setPage(1);
          }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon />
              </InputAdornment>
            ),
          }}
          sx={{ width: "40%" }}
        />
        <Button
          startIcon={<AddCircleOutlineIcon />}
          variant="contained"
          onClick={handleOpen}
        >
          Add New
        </Button>
      </Stack>

      {loading ? (
        <Box textAlign="center" mt={5}>
          <CircularProgress />
        </Box>
      ) : paginatedData.length === 0 ? (
        <Typography textAlign="center" mt={5}>
          No data found.
        </Typography>
      ) : (
        paginatedData.map((item, index) => (
          <Accordion key={index}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}>
              <Typography sx={{ fontWeight: "bold" }}>
                {item.problem}
              </Typography>
            </AccordionSummary>
            <AccordionDetails>
              <Typography sx={{ mb: 1 }} color="text.secondary">
                Application: {getAppNameById(item.applicationId)}
              </Typography>
              <Typography>{item.solution}</Typography>
            </AccordionDetails>
          </Accordion>
        ))
      )}

      <Stack alignItems="center" mt={3}>
        <Pagination
          count={Math.ceil(filteredData.length / itemsPerPage)}
          page={page}
          onChange={(e, value) => setPage(value)}
        />
      </Stack>

      {/* Dialog Add */}
      <Dialog open={open} onClose={handleClose} fullWidth>
        <DialogTitle>Add Knowledge Item</DialogTitle>
        <DialogContent>
          <FormControl fullWidth sx={{ mt: 2 }}>
            <InputLabel>Application</InputLabel>
            <Select
              value={appForNew}
              label="Application"
              onChange={(e) => setAppForNew(e.target.value)}
            >
              {applications.map((app) => (
                <MenuItem key={app.id} value={app.id}>
                  {app.name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <TextField
            label="Problem"
            fullWidth
            value={problem}
            onChange={(e) => setProblem(e.target.value)}
            sx={{ mt: 2 }}
          />
          <TextField
            label="Solution"
            fullWidth
            multiline
            minRows={3}
            value={solution}
            onChange={(e) => setSolution(e.target.value)}
            sx={{ mt: 2 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button variant="contained" onClick={handleSubmit}>
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default KnowledgeCenter;
