import React, { useContext } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthContext } from "./context/AuthContext";
import ProtectedRoute from "./components/ProtectedRoute";
import LoginPage from "./pages/LoginPage";
import TaskListPage from "./pages/TaskListPage";
import CreateTaskPage from "./pages/CreateTaskPage";
import CreateDeploymentRequestPage from "./pages/CreateDeploymentRequestPage";
import CreateDeploymentSupportPage from "./pages/CreateDeploymentSupportPage";
import MainLayout from "./layout/MainLayout";
import DeploymentBoardPage from "./pages/DeploymentBoardPage";
import KnowledgeCenter from "./pages/KnowledgeCenter";

const App = () => {
  const { isAuthenticated } = useContext(AuthContext);

  return (
    <Router>
      <Routes>
        {/* Login page */}
        <Route path="/login" element={<LoginPage />} />

        {/* Protected Routes */}
        <Route
          path="/tasks"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <MainLayout>
                <TaskListPage />
              </MainLayout>
            </ProtectedRoute>
          }
        />

        <Route
          path="/create"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <MainLayout>
                <CreateTaskPage />
              </MainLayout>
            </ProtectedRoute>
          }
        />

        <Route
          path="/request-deployment"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <MainLayout>
                <CreateDeploymentRequestPage />
              </MainLayout>
            </ProtectedRoute>
          }
        />

        <Route
          path="/create-deployment-support"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <MainLayout>
                <CreateDeploymentSupportPage />
              </MainLayout>
            </ProtectedRoute>
          }
        />

        <Route
          path="/deployment-board"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <MainLayout>
                <DeploymentBoardPage />
              </MainLayout>
            </ProtectedRoute>
          }
        />

        <Route
          path="/knowledge-center"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <MainLayout>
                <KnowledgeCenter />
              </MainLayout>
            </ProtectedRoute>
          }
        />




        {/* ðŸ”¹ Default redirect */}
        <Route path="/" element={<Navigate to="/tasks" replace />} />
      </Routes>
    </Router>
  );
};

export default App;
