import React, { useState, useEffect } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  TextField,
  Button,
  CircularProgress,
  IconButton,
  Stack,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Pagination,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import SaveIcon from "@mui/icons-material/Save";
import CloseIcon from "@mui/icons-material/Close";

const KnowledgeCenter = () => {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  // form tambah data
  const [problem, setProblem] = useState("");
  const [solution, setSolution] = useState("");

  // edit data
  const [editId, setEditId] = useState(null);
  const [editProblem, setEditProblem] = useState("");
  const [editSolution, setEditSolution] = useState("");

  // delete dialog
  const [deleteId, setDeleteId] = useState(null);
  const [openConfirm, setOpenConfirm] = useState(false);

  // pagination
  const [page, setPage] = useState(1);
  const itemsPerPage = 10;

  // ambil data
  const fetchData = async (searchTerm = "") => {
    setLoading(true);
    try {
      const res = await api.get(`/knowledge-center?search=${searchTerm}`);
      setData(res.data.data || []);
    } catch (err) {
      console.error("‚ùå Gagal mengambil data:", err);
    } finally {
      setLoading(false);
    }
  };

  // debounce live search
  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      fetchData(search);
      setPage(1);
    }, 500);
    return () => clearTimeout(delayDebounce);
  }, [search]);

  // tambah data
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!problem || !solution) return;
    try {
      await api.post("/knowledge-center", { problem, solution });
      setProblem("");
      setSolution("");
      fetchData();
    } catch (err) {
      console.error("‚ùå Gagal menambah data:", err);
    }
  };

  // edit data
  const handleEdit = (item) => {
    setEditId(item.id);
    setEditProblem(item.problem);
    setEditSolution(item.solution);
  };

  const handleCancelEdit = () => {
    setEditId(null);
    setEditProblem("");
    setEditSolution("");
  };

  const handleSaveEdit = async (id) => {
    try {
      await api.put(`/knowledge-center/${id}`, {
        problem: editProblem,
        solution: editSolution,
      });
      setEditId(null);
      fetchData();
    } catch (err) {
      console.error("‚ùå Gagal memperbarui data:", err);
    }
  };

  // hapus data
  const confirmDelete = (id) => {
    setDeleteId(id);
    setOpenConfirm(true);
  };

  const handleDelete = async () => {
    try {
      await api.delete(`/knowledge-center/${deleteId}`);
      setOpenConfirm(false);
      fetchData();
    } catch (err) {
      console.error("‚ùå Gagal menghapus data:", err);
    }
  };

  // pagination
  const handlePageChange = (event, value) => {
    setPage(value);
  };

  const startIndex = (page - 1) * itemsPerPage;
  const paginatedData = data.slice(startIndex, startIndex + itemsPerPage);
  const totalPages = Math.ceil(data.length / itemsPerPage);

  return (
    <Box p={4}>
      <Typography variant="h5" fontWeight="bold" mb={3}>
        üìò Knowledge Center
      </Typography>

      {/* üîç Live Search */}
      <TextField
        label="Cari Problem..."
        variant="outlined"
        fullWidth
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        sx={{ mb: 3 }}
      />

      {/* ‚ûï Tambah Data */}
      <Box
        component="form"
        onSubmit={handleSubmit}
        sx={{ mb: 4, display: "flex", gap: 2, flexWrap: "wrap" }}
      >
        <TextField
          label="Problem"
          variant="outlined"
          fullWidth
          value={problem}
          onChange={(e) => setProblem(e.target.value)}
        />
        <TextField
          label="Solution"
          variant="outlined"
          fullWidth
          value={solution}
          onChange={(e) => setSolution(e.target.value)}
        />
        <Button
          type="submit"
          variant="contained"
          sx={{ minWidth: "120px", backgroundColor: "#1976d2" }}
        >
          Simpan
        </Button>
      </Box>

      {/* üîÑ Loading */}
      {loading ? (
        <CircularProgress />
      ) : data.length === 0 ? (
        <Typography color="text.secondary">Belum ada data.</Typography>
      ) : (
        <>
          {paginatedData.map((item) => (
            <Accordion key={item.id} sx={{ mb: 2 }}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                {editId === item.id ? (
                  <TextField
                    fullWidth
                    variant="standard"
                    value={editProblem}
                    onChange={(e) => setEditProblem(e.target.value)}
                  />
                ) : (
                  <Typography fontWeight="bold">{item.problem}</Typography>
                )}
              </AccordionSummary>

              <AccordionDetails>
                {editId === item.id ? (
                  <>
                    <TextField
                      fullWidth
                      multiline
                      minRows={2}
                      variant="standard"
                      value={editSolution}
                      onChange={(e) => setEditSolution(e.target.value)}
                      sx={{ mb: 2 }}
                    />
                    <Stack direction="row" spacing={1}>
                      <IconButton
                        component="div"
                        onClick={() => handleSaveEdit(item.id)}
                      >
                        <SaveIcon color="success" />
                      </IconButton>
                      <IconButton component="div" onClick={handleCancelEdit}>
                        <CloseIcon color="error" />
                      </IconButton>
                    </Stack>
                  </>
                ) : (
                  <>
                    <Typography sx={{ mb: 2 }}>{item.solution}</Typography>
                    <Stack direction="row" spacing={1}>
                      <IconButton component="div" onClick={() => handleEdit(item)}>
                        <EditIcon color="primary" />
                      </IconButton>
                      <IconButton component="div" onClick={() => confirmDelete(item.id)}>
                        <DeleteIcon color="error" />
                      </IconButton>
                    </Stack>
                  </>
                )}
              </AccordionDetails>
            </Accordion>
          ))}

          {/* üìÑ Pagination */}
          <Box display="flex" justifyContent="center" mt={3}>
            <Pagination
              count={totalPages}
              page={page}
              onChange={handlePageChange}
              color="primary"
            />
          </Box>
        </>
      )}

      {/* ‚ö†Ô∏è Dialog Konfirmasi Hapus */}
      <Dialog open={openConfirm} onClose={() => setOpenConfirm(false)}>
        <DialogTitle>Hapus Data</DialogTitle>
        <DialogContent>
          <Typography>Apakah Anda yakin ingin menghapus data ini?</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenConfirm(false)}>Batal</Button>
          <Button onClick={handleDelete} color="error" variant="contained">
            Hapus
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default KnowledgeCenter;
