import React, { useEffect, useState } from "react";
import api from "../services/api";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import {
  Box,
  Typography,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
} from "@mui/material";
import DownloadIcon from "@mui/icons-material/Download";

const riskColors = {
  "Major Release": "#000000",
  High: "#e53935",
  Medium: "#fdd835",
  Low: "#43a047",
  cancel: "#9e9e9e",
  default: "#90a4ae",
};

// helper: ambil YYYY-MM-DD dari input tanggal apa pun (ISO atau dengan waktu)
const toDateString = (dateInput) => {
  if (!dateInput) return null;
  const d = new Date(dateInput);
  if (isNaN(d)) return null;
  // ambil bagian tahun/bulan/tanggal di zona lokal untuk mencegah shift timezone
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  return `${y}-${m}-${day}`;
};

const DeploymentBoardPage = () => {
  const [events, setEvents] = useState([]); // events dengan start = "YYYY-MM-DD"
  const [loading, setLoading] = useState(true);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [statuses] = useState(["null", "success", "redeploy", "cancel"]);
  const [selectedStatus, setSelectedStatus] = useState("");
  const [sqiPics, setSqiPics] = useState([]);
  const [selectedPic, setSelectedPic] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);

  const user = JSON.parse(localStorage.getItem("user"));
  const role = user?.role?.toLowerCase() || "guest";

  // ðŸ”¹ Ambil data dari API
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [calendarRes, sqiRes] = await Promise.all([
          api.get("/calendar"),
          api.get("/sqi-pics"),
        ]);

        const rawData = calendarRes.data?.data || [];
        const cleaned = rawData
          .map((item) => {
            // ambil field tanggal yang ada
            const dateField = item.implementDate || item.supportDate || item.date;
            if (!dateField) return null;

            const dateStr = toDateString(dateField); // YYYY-MM-DD sebagai string lokal
            if (!dateStr) return null;

            return {
              id: item.id,
              title: item.title,
              start: dateStr, // penting: simpan sebagai date-only string
              allDay: true,
              extendedProps: item,
            };
          })
          .filter(Boolean);

        setEvents(cleaned);
        setSqiPics(sqiRes.data?.data || []);
      } catch (err) {
        console.error("âŒ Gagal mengambil data:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // ðŸ”¹ Klik event
  const handleEventClick = (clickInfo) => {
    // FullCalendar memberikan extendedProps yang kita simpan di event
    const eventProps = clickInfo.event.extendedProps;
    setSelectedEvent(eventProps);
    setSelectedStatus(eventProps.status || "null");
    setSelectedPic(eventProps.sqiPicId || "");
    setOpenDialog(true);
  };

  // ðŸ”¹ Download attachment
  const handleDownload = async () => {
    try {
      if (!selectedEvent?.attachment) return;
      const filename = selectedEvent.attachment.split("/").pop();
      const response = await api.get(`/deployment-requests/download/${filename}`, {
        responseType: "blob",
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error("âŒ Gagal mengunduh file:", err);
      alert("Gagal mengunduh file.");
    }
  };

  // ðŸ”¹ Update status
  const handleStatusChange = async (event) => {
    if (isUpdating) return;
    setIsUpdating(true);
    const newStatus = event.target.value;
    setSelectedStatus(newStatus);

    try {
      await api.patch(`/deployment-requests/${selectedEvent.id}`, {
        status: newStatus === "null" ? null : newStatus,
      });
      setSelectedEvent((prev) => ({
        ...prev,
        status: newStatus === "null" ? null : newStatus,
      }));
      alert("Status berhasil diubah!");
    } catch (err) {
      console.error("âŒ Gagal memperbarui status:", err);
      alert("Gagal memperbarui status.");
    } finally {
      setIsUpdating(false);
    }
  };

  // ðŸ”¹ Assign PIC
  const handleAssignPic = async (event) => {
    if (isUpdating) return;
    setIsUpdating(true);
    const picId = event.target.value;
    setSelectedPic(picId);

    try {
      await api.patch(`/deployment-requests/${selectedEvent.id}`, {
        sqiPicId: picId === "" ? null : picId,
      });
      setSelectedEvent((prev) => ({
        ...prev,
        sqiPicId: picId === "" ? null : picId,
      }));
      alert("PIC berhasil di-assign!");
    } catch (err) {
      console.error("âŒ Gagal assign PIC:", err);
      alert("Gagal assign PIC.");
    } finally {
      setIsUpdating(false);
    }
  };

  // ðŸ”¹ Custom isi tiap sel tanggal
  const dayCellContent = (arg) => {
    // arg.date adalah objek Date; kita buat dateStr dalam format YYYY-MM-DD (lokal)
    const dateStr = toDateString(arg.date);
    const dayEvents = events.filter((e) => e.start === dateStr); // bandingkan string === string

    if (dayEvents.length === 0) {
      return (
        <div style={{ padding: "4px 6px" }}>
          <div style={{ fontSize: "0.8rem", fontWeight: "bold" }}>
            {arg.dayNumberText}
          </div>
        </div>
      );
    }

    const requests = dayEvents.filter((e) => e.extendedProps.type === "request");
    const supports = dayEvents.filter((e) => e.extendedProps.type === "support");

    const renderEvent = (ev) => {
      const color =
        ev.extendedProps.status === "cancel"
          ? riskColors.cancel
          : riskColors[ev.extendedProps.riskImpact] || riskColors.default;

      const textStyle =
        ev.extendedProps.status === "cancel"
          ? { textDecoration: "line-through", color: "#9e9e9e" }
          : { color: "#333" };

      return (
        <div
          key={ev.id}
          onClick={() =>
            handleEventClick({ event: { extendedProps: ev.extendedProps } })
          }
          style={{
            display: "flex",
            alignItems: "center",
            gap: "6px",
            cursor: "pointer",
            fontSize: "0.75rem",
            fontWeight: 500,
            margin: "2px 0",
          }}
        >
          <span
            style={{
              width: "8px",
              height: "8px",
              borderRadius: "50%",
              backgroundColor: color,
              display: "inline-block",
            }}
          />
          <span style={textStyle}>{ev.title}</span>
        </div>
      );
    };

    return (
      <div
        style={{
          padding: "4px 6px",
          borderRadius: "6px",
          transition: "background-color 0.2s",
          minHeight: "80px",
        }}
      >
        <div
          style={{
            fontSize: "0.8rem",
            fontWeight: "bold",
            marginBottom: "4px",
            textAlign: "left",
          }}
        >
          {arg.dayNumberText}
        </div>

        <div
          style={{
            borderBottom: "1px solid #e0e0e0",
            paddingBottom: "4px",
            marginBottom: "4px",
          }}
        >
          <div
            style={{
              fontSize: "0.7rem",
              textAlign: "center",
              fontWeight: "bold",
              color: "#1565c0",
              marginBottom: "2px",
            }}
          >
            Request
          </div>
          {requests.length > 0 && requests.map(renderEvent)}
        </div>

        <div>
          <div
            style={{
              fontSize: "0.7rem",
              textAlign: "center",
              fontWeight: "bold",
              color: "#2e7d32",
              marginBottom: "2px",
            }}
          >
            Support
          </div>
          {supports.length > 0 && supports.map(renderEvent)}
        </div>
      </div>
    );
  };

  if (loading)
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
        }}
      >
        <CircularProgress />
      </Box>
    );

  return (
    <Box sx={{ px: 2, pb: 5 }}>
      <Typography
        variant="h5"
        fontWeight="bold"
        sx={{
          color: "#1976d2",
          mb: 3,
          textAlign: "center",
          letterSpacing: "0.5px",
        }}
      >
        Deployment Calendar
      </Typography>

      <FullCalendar
      plugins={[dayGridPlugin, interactionPlugin]}
      initialView="dayGridMonth"
      headerToolbar={{
        left: "prev,next today",
        center: "title",
        right: "dayGridMonth,dayGridWeek",
      }}
      height="80vh"
      events={events}
      eventClick={handleEventClick}
      dayCellContent={dayCellContent}
      timeZone="local"         // âœ… pastikan tanggal tidak bergeser
      eventDisplay="none"      // âœ… cegah event ganda di-render
    />


      {/* Dialog detail */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)} fullWidth>
        <DialogTitle>Detail Event</DialogTitle>
        <DialogContent dividers>
          {selectedEvent ? (
            <Box sx={{ display: "flex", flexDirection: "column", gap: 1 }}>
              <Typography>
                <strong>Release ID:</strong> {selectedEvent.releaseId}
              </Typography>
              <Typography>
                <strong>Judul:</strong> {selectedEvent.title}
              </Typography>
              <Typography>
                <strong>Tanggal:</strong>{" "}
                {selectedEvent.implementDate || selectedEvent.supportDate || selectedEvent.date}
              </Typography>
              <Typography>
                <strong>Risk Impact:</strong> {selectedEvent.riskImpact || "-"}
              </Typography>

              {role === "sqi" && selectedEvent?.type === "request" && (
                <FormControl size="small" sx={{ mt: 1, width: "100%" }}>
                  <InputLabel id="sqi-pic-label">Assign SQI PIC</InputLabel>
                  <Select
                    labelId="sqi-pic-label"
                    value={selectedPic}
                    onChange={handleAssignPic}
                    label="Assign SQI PIC"
                  >
                    {sqiPics.map((pic) => (
                      <MenuItem key={pic.id} value={pic.id}>
                        {pic.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              )}

              {role === "developer" && (
                <Typography sx={{ mt: 1 }}>
                  <strong>PIC SQI:</strong>{" "}
                  {sqiPics.find((p) => p.id === selectedEvent.sqiPicId)?.name ||
                    "-"}
                </Typography>
              )}

              {role === "sqi" && selectedEvent?.type === "request" && (
                <FormControl size="small" sx={{ mt: 2, width: "100%" }}>
                  <InputLabel id="status-label">Status</InputLabel>
                  <Select
                    labelId="status-label"
                    value={selectedStatus}
                    onChange={handleStatusChange}
                    label="Status"
                  >
                    {statuses.map((status) => (
                      <MenuItem key={status} value={status}>
                        {status === "null" ? "-" : status}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              )}

              {role === "developer" && (
                <Typography sx={{ mt: 1 }}>
                  <strong>Status:</strong> {selectedEvent.status || "-"}
                </Typography>
              )}
            </Box>
          ) : (
            <Typography>Tidak ada data.</Typography>
          )}
        </DialogContent>

        <DialogActions>
          {selectedEvent?.attachment && (
            <Button
              variant="outlined"
              color="primary"
              size="small"
              startIcon={<DownloadIcon />}
              onClick={handleDownload}
            >
              Download
            </Button>
          )}
          <Button onClick={() => setOpenDialog(false)} color="primary">
            Tutup
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default DeploymentBoardPage;
