import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  CircularProgress,
  Paper,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
} from "@mui/material";
import DownloadIcon from "@mui/icons-material/Download";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";

const DeploymentCalendarPage = () => {
  const [deploymentRequests, setDeploymentRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);

  const handleEventClick = (info) => {
    const { title, extendedProps } = info.event;

    setSelectedEvent({
      title,
      implementDate: extendedProps.implementDate,
      applicationName: extendedProps.applicationName,
      riskImpact: extendedProps.riskImpact,
      attachment: extendedProps.attachment, // ‚úÖ tambahkan ini
    });
    setOpenDialog(true);
  };


  useEffect(() => {
    const fetchDeploymentRequests = async () => {
      try {
        const res = await api.get("/deployment-requests", {
          params: {
            startDate: new Date().toISOString().split("T")[0],
            endDate: new Date(new Date().setMonth(new Date().getMonth() + 1))
              .toISOString()
              .split("T")[0],
          },
        });
        const requests = res.data?.data || res.data || [];
        console.log("üß© Deployment Requests Data:", deploymentRequests);
        setDeploymentRequests(requests);
      } catch (err) {
        console.error("‚ùå Gagal mengambil data deployment requests:", err);
        setDeploymentRequests([]);
      } finally {
        setLoading(false);
      }
    };

    fetchDeploymentRequests();
  }, []);

  const calendarEvents = (deploymentRequests || []).map((request) => ({
    title: `${request.title} - ${request.riskImpact}`,
    date: request.implementDate,
    implementDate: request.implementDate,
    applicationName: request.application?.name || "Unknown",
    riskImpact: request.riskImpact,
    attachment: request.attachment,
    backgroundColor:
      request.riskImpact === "Low"
        ? "#A5D6A7"
        : request.riskImpact === "Medium"
        ? "#FFF59D"
        : request.riskImpact === "High"
        ? "#EF9A9A"
        : request.riskImpact === "Major Release"
        ? "#3a3a3aff"
        : "#E0E0E0",
    textColor:
      request.riskImpact === "Major Release" ? "#FFFFFF" : "#000000", // putih untuk teksnya
  }));


  const handleDownload = async () => {
  try {
    if (!selectedEvent?.attachment) return;

    const filename = selectedEvent.attachment.split("/").pop();
    const response = await api.get(`/deployment-requests/download/${filename}`, {
      responseType: "blob",
    });

    const url = window.URL.createObjectURL(new Blob([response.data]));
    const link = document.createElement("a");
    link.href = url;
    link.setAttribute("download", filename);
    document.body.appendChild(link);
    link.click();
    link.parentNode.removeChild(link);
  } catch (err) {
    console.error("‚ùå Gagal mengunduh file:", err);
    alert("Gagal mengunduh file.");
  }
  };

  if (loading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
          marginLeft: "220px",
        }}
      >
        <CircularProgress size={50} />
      </Box>
    );
  }

  return (
    <Box sx={{ display: "flex" }}>
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          marginLeft: "220px",
          minHeight: "100vh",
          backgroundColor: "transparent",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Typography
          variant="h5"
          fontWeight="bold"
          sx={{
            color: "#1976d2",
            mb: 3,
            textAlign: "center",
            mt: 2,
          }}
        >
          Kalender Request Deployment
        </Typography>

        <Paper
          elevation={3}
          sx={{
            width: "90%",
            maxWidth: 900,
            p: 2,
            borderRadius: 2,
          }}
        >
          <FullCalendar
            plugins={[dayGridPlugin]}
            initialView="dayGridMonth"
            height="80vh"
            events={calendarEvents}
            eventDisplay="block"
            eventContent={(arg) => (
              <div
                style={{
                  color:
                    arg.event.extendedProps.riskImpact === "Major Release"
                      ? "#fff"
                      : "#000",
                }}
              >
                {arg.event.title}
              </div>
            )}
            eventBorderColor="#00000030"
            displayEventTime={false} // ‚¨ÖÔ∏è Hilangkan jam "7a"
            eventClick={handleEventClick} // ‚¨ÖÔ∏è ketika event diklik
            eventDidMount={(info) => {
              // Agar teks terlihat lebih rapi di tengah
              info.el.style.padding = "4px 6px";
              info.el.style.borderRadius = "4px";
              info.el.style.fontWeight = "600";
              info.el.style.textAlign = "center";
            }}
          />

          <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
            <DialogTitle>Detail Deployment Request</DialogTitle>
            <DialogContent dividers>
              {selectedEvent ? (
                <Box sx={{ display: "flex", flexDirection: "column", gap: 1 }}>
                  <Typography><strong>Judul:</strong> {selectedEvent.title}</Typography>
                  <Typography><strong>Aplikasi:</strong> {selectedEvent.applicationName}</Typography>
                  <Typography><strong>Tanggal Implementasi:</strong> {selectedEvent.implementDate}</Typography>
                  <Typography><strong>Risk Impact:</strong> {selectedEvent.riskImpact}</Typography>
                </Box>
              ) : (
                <Typography>Tidak ada data.</Typography>
              )}
            </DialogContent>
            <DialogActions>
              {selectedEvent?.attachment && (
              <Button
                variant="outlined"
                color="primary"
                size="small"
                startIcon={<DownloadIcon />}
                component="a"
                href={`http://localhost:4000/${selectedEvent.attachment?.replace(/\\/g, "/")}`}
                target="_blank"
                rel="noopener noreferrer"
                download
                sx={{
                  textTransform: "none",
                  fontWeight: 500,
                }}
              >
                Download
              </Button>
              )}
              <Button onClick={() => setOpenDialog(false)} color="primary">
                Tutup
              </Button>
            </DialogActions>
          </Dialog>
        </Paper>
      </Box>
    </Box>
  );
};

export default DeploymentCalendarPage;
