import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  CircularProgress,
  Pagination,
  Grid,
  Paper,
} from "@mui/material";

const ITEMS_PER_PAGE = 6;

export default function DeploymentBoardPage() {
  const [requests, setRequests] = useState([]);
  const [supports, setSupports] = useState([]);
  const [combinedData, setCombinedData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);

  // Fetch data dari 2 endpoint
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [reqRes, supRes] = await Promise.all([
          api.get("/deployment-requests"),
          api.get("/supports"),
        ]);

        const reqData = reqRes.data.map((item) => ({
          ...item,
          type: "Request",
          createdAt: new Date(item.created_at),
        }));
        const supData = supRes.data.map((item) => ({
          ...item,
          type: "Support",
          createdAt: new Date(item.created_at),
        }));

        // Gabung dan urutkan berdasarkan tanggal
        const merged = [...reqData, ...supData].sort(
          (a, b) => b.createdAt - a.createdAt
        );
        setRequests(reqData);
        setSupports(supData);
        setCombinedData(merged);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Pagination logic
  const totalPages = Math.ceil(combinedData.length / ITEMS_PER_PAGE);
  const paginatedData = combinedData.slice(
    (currentPage - 1) * ITEMS_PER_PAGE,
    currentPage * ITEMS_PER_PAGE
  );

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" mt={8}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box p={3}>
      <Typography variant="h5" fontWeight="bold" mb={3}>
        Deployment Board
      </Typography>

      <Grid container spacing={2}>
        {paginatedData.map((item, index) => (
          <Grid item xs={12} md={6} lg={4} key={index}>
            <Paper
              sx={{
                p: 2,
                borderLeft: `6px solid ${
                  item.type === "Request" ? "#1976d2" : "#2e7d32"
                }`,
                boxShadow: 2,
              }}
            >
              <Typography variant="subtitle1" fontWeight="bold">
                {item.title || "(No Title)"}
              </Typography>
              <Typography variant="body2" color="text.secondary" mb={1}>
                {item.type}
              </Typography>
              <Typography variant="body2">
                {item.description || "No description"}
              </Typography>
              <Typography
                variant="caption"
                color="text.secondary"
                sx={{ display: "block", mt: 1 }}
              >
                {item.createdAt.toLocaleDateString("en-GB")}
              </Typography>
            </Paper>
          </Grid>
        ))}
      </Grid>

      <Box display="flex" justifyContent="center" mt={3}>
        <Pagination
          count={totalPages}
          page={currentPage}
          onChange={(_, page) => setCurrentPage(page)}
          color="primary"
        />
      </Box>
    </Box>
  );
}
