import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  CircularProgress,
  Paper,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
} from "@mui/material";
import DownloadIcon from "@mui/icons-material/Download";

const DeploymentCalendarPage = () => {
  const [deploymentRequests, setDeploymentRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [statuses] = useState(["null", "success", "redeploy", "cancel"]);
  const [sqiPics, setSqiPics] = useState([]);
  const [selectedStatus, setSelectedStatus] = useState("");
  const [selectedPic, setSelectedPic] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);

  const role = "SQI";

  const handleCardClick = (request) => {
    setSelectedEvent(request);
    setSelectedStatus(request.status || "null");
    setSelectedPic(request.sqiPicId || "");
    setOpenDialog(true);
  };

  const fetchDeploymentRequests = async () => {
    setLoading(true);
    try {
      const res = await api.get("/deployment-requests", {
        params: {
          startDate: new Date().toISOString().split("T")[0],
          endDate: new Date(new Date().setMonth(new Date().getMonth() + 1))
            .toISOString()
            .split("T")[0],
        },
      });
      setDeploymentRequests(res.data?.data || res.data || []);
    } catch (err) {
      console.error("âŒ Gagal mengambil data deployment requests:", err);
      setDeploymentRequests([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const fetchSqiPics = async () => {
      try {
        const res = await api.get("/sqi-pics");
        setSqiPics(res.data?.data || res.data || []);
      } catch (err) {
        console.error("âŒ Gagal mengambil data SQI PIC:", err);
      }
    };

    fetchDeploymentRequests();
    if (role === "SQI") fetchSqiPics();
  }, []);

  const handleDownload = async () => {
    try {
      if (!selectedEvent?.attachment) return;
      const filename = selectedEvent.attachment.split("/").pop();
      const response = await api.get(`/deployment-requests/download/${filename}`, {
        responseType: "blob",
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error("âŒ Gagal mengunduh file:", err);
      alert("Gagal mengunduh file.");
    }
  };

  const handleStatusChange = async (event) => {
    if (isUpdating) return;
    setIsUpdating(true);
    const newStatus = event.target.value;
    setSelectedStatus(newStatus);

    try {
      await api.patch(`/deployment-requests/${selectedEvent.id}`, {
        status: newStatus === "null" ? null : newStatus,
      });
      await fetchDeploymentRequests();
      setSelectedEvent((prev) => ({ ...prev, status: newStatus === "null" ? null : newStatus }));
      alert("Status berhasil diperbarui!");
    } catch (err) {
      console.error("âŒ Gagal memperbarui status:", err);
      alert("Gagal memperbarui status.");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleAssignPic = async (event) => {
    if (isUpdating) return;
    setIsUpdating(true);
    const picId = event.target.value;
    setSelectedPic(picId);

    try {
      await api.patch(`/deployment-requests/${selectedEvent.id}`, {
        sqiPicId: picId === "" ? null : picId,
      });
      await fetchDeploymentRequests();
      setSelectedEvent((prev) => ({ ...prev, sqiPicId: picId === "" ? null : picId }));
      alert("PIC berhasil di-assign!");
    } catch (err) {
      console.error("âŒ Gagal assign PIC:", err);
      alert("Gagal assign PIC.");
    } finally {
      setIsUpdating(false);
    }
  };

  // ðŸ”„ Kelompokkan data berdasarkan hari (Seninâ€“Minggu)
  const groupedByDay = ["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"]
    .map((day) => ({
      day,
      requests: deploymentRequests.filter((req) => {
        const reqDay = new Date(req.implementDate).toLocaleDateString("en-US", { weekday: "long" });
        return reqDay === day;
      }),
    }));

  const getColor = (riskImpact) => {
    switch (riskImpact) {
      case "Low": return "#A5D6A7";
      case "Medium": return "#FFF59D";
      case "High": return "#EF9A9A";
      case "Major Release": return "#3a3a3aff";
      default: return "#E0E0E0";
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh", marginLeft: "220px" }}>
        <CircularProgress size={50} />
      </Box>
    );
  }

  return (
    <Box sx={{ display: "flex", overflowX: "auto", height: "100vh", p: 3, ml: "220px", gap: 2 }}>
      {groupedByDay.map(({ day, requests }) => (
        <Paper
          key={day}
          elevation={4}
          sx={{
            minWidth: 260,
            flexShrink: 0,
            borderRadius: 2,
            display: "flex",
            flexDirection: "column",
            p: 2,
            backgroundColor: "#f8f9fa",
          }}
        >
          <Typography variant="h6" align="center" sx={{ mb: 2, color: "#1976d2", fontWeight: "bold" }}>
            {day}
          </Typography>

          <Box sx={{ display: "flex", flexDirection: "column", gap: 1 }}>
            {requests.length > 0 ? (
              requests.map((req) => (
                <Paper
                  key={req.id}
                  sx={{
                    backgroundColor: getColor(req.riskImpact),
                    p: 1.2,
                    borderRadius: 1.5,
                    cursor: "pointer",
                    fontSize: 14,
                    fontWeight: 600,
                    textAlign: "center",
                    color: req.riskImpact === "Major Release" ? "#fff" : "#000",
                    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
                    "&:hover": { transform: "scale(1.02)" },
                    transition: "0.15s",
                  }}
                  onClick={() => handleCardClick(req)}
                >
                  {req.title}
                </Paper>
              ))
            ) : (
              <Typography variant="body2" sx={{ color: "#9e9e9e", textAlign: "center" }}>
                No requests
              </Typography>
            )}
          </Box>
        </Paper>
      ))}

      {/* âœ… Dialog Detail (dipertahankan sama persis) */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)} fullWidth>
        <DialogTitle>Detail Deployment Request</DialogTitle>
        <DialogContent dividers>
          {selectedEvent ? (
            <Box sx={{ display: "flex", flexDirection: "column", gap: 1 }}>
              <Typography><strong>Judul:</strong> {selectedEvent.title}</Typography>
              <Typography><strong>Aplikasi:</strong> {selectedEvent.application?.name || "Unknown"}</Typography>
              <Typography><strong>Tanggal Implementasi:</strong> {selectedEvent.implementDate}</Typography>
              <Typography><strong>Risk Impact:</strong> {selectedEvent.riskImpact}</Typography>

              {role === "SQI" && (
                <FormControl size="small" sx={{ mt: 1, width: "100%" }}>
                  <InputLabel id="sqi-pic-label">Assign SQI PIC</InputLabel>
                  <Select
                    labelId="sqi-pic-label"
                    value={selectedPic}
                    onChange={handleAssignPic}
                    label="Assign SQI PIC"
                  >
                    {sqiPics.map((pic) => (
                      <MenuItem key={pic.id} value={pic.id}>{pic.name}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
              )}
            </Box>
          ) : (
            <Typography>Tidak ada data.</Typography>
          )}
        </DialogContent>

        <DialogActions>
          {selectedEvent?.attachment && (
            <Button
              variant="outlined"
              color="primary"
              size="small"
              startIcon={<DownloadIcon />}
              onClick={handleDownload}
              sx={{ textTransform: "none", fontWeight: 500 }}
            >
              Download
            </Button>
          )}

          {role === "SQI" && (
            <FormControl size="small" sx={{ minWidth: 120, mr: 2 }}>
              <InputLabel id="status-label">Status</InputLabel>
              <Select
                labelId="status-label"
                value={selectedStatus}
                onChange={handleStatusChange}
                label="Status"
              >
                {statuses.map((status) => (
                  <MenuItem key={status} value={status}>
                    {status === "null" ? "-" : status}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          )}
          <Button onClick={() => setOpenDialog(false)} color="primary">
            Tutup
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default DeploymentCalendarPage;
