import React, { useEffect, useState } from "react";
import axios from "axios";
import { Container, Typography, Button, Table, TableBody, TableCell, TableHead, TableRow } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { isAuthenticated } from "../middleware/auth"; // Cek otentikasi
import MainLayout from "../layout/MainLayout";


const TaskListPage = () => {
  const [tasks, setTasks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // // Cek apakah user sudah login (JWT)
    // if (!isAuthenticated()) {
    //   navigate("/login"); // Redirect ke login jika belum login
    //   return;
    // }

    const fetchTasks = async () => {
      try {
        const res = await axios.get("http://localhost:4000/api/tasks", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        setTasks(res.data);
      } catch (err) {
        console.error("Gagal mengambil data task:", err);
      }
    };
    fetchTasks();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token"); // Hapus token saat logout
    navigate("/login"); // Redirect ke login
  };

  <MainLayout>
  
</MainLayout>

  return (
    <Container sx={{ mt: 5 }}>
      <Typography variant="h5" gutterBottom>
        Daftar Task
      </Typography>

      {/* Tombol Logout */}
      <Button
        variant="contained"
        color="secondary"
        onClick={handleLogout}
        sx={{ mb: 2 }}
      >
        Logout
      </Button>

      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Judul</TableCell>
            <TableCell>Deskripsi</TableCell>
            <TableCell>Aplikasi</TableCell>
            <TableCell>PIC SQI</TableCell>
            <TableCell>Bentuk Support</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {tasks.length > 0 ? (
            tasks.map((task) => (
              <TableRow key={task.id}>
                <TableCell>{task.title}</TableCell>
                <TableCell>{task.description}</TableCell>
                <TableCell>{task.application?.name || "—"}</TableCell>
                <TableCell>{task.sqiPic?.name || "—"}</TableCell>
                <TableCell>
                  {task.supportType ? task.supportType.name : task.customSupportType || "—"}
                </TableCell>
              </TableRow>
            ))
          ) : (
            <TableRow>
              <TableCell colSpan={5} align="center">
                Belum ada task.
              </TableCell>
            </TableRow>
          )}
        </TableBody>
      </Table>
    </Container>
  );
};

export default TaskListPage;
