import React, { useContext } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthContext } from "./context/AuthContext";
import ProtectedRoute from "./components/ProtectedRoute";
import LoginPage from "./pages/LoginPage";
import TaskListPage from "./pages/TaskListPage";
import CreateTaskPage from "./pages/CreateTaskPage";
import MainLayout from "./layout/MainLayout";
import DeploymentCalendarPage from "./pages/DeploymentCalendarPage";
import CreateDeploymentRequestPage from "./pages/CreateDeploymentRequestPage";
import CreateDeploymentSupportPage from "./pages/CreateDeploymentSupportPage";
import DeploymentCalendarPage2 from "./pages/DeploymentCalendarPage2";
import DeploymentCalendarPage3 from "./pages/DeploymentCalendarPage3";

const App = () => {
  const { isAuthenticated } = useContext(AuthContext);

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage />} />
        <Route
          path="/tasks"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <MainLayout>
                <TaskListPage />
              </MainLayout>
            </ProtectedRoute>
          }
        />
        <Route
          path="/create"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <MainLayout>
                <CreateTaskPage />
              </MainLayout>
            </ProtectedRoute>
          }
        />

        {/* ðŸ”¹ Create Deployment Request (developer only) */}
        <Route
          path="/request-deployment"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <MainLayout>
                <CreateDeploymentRequestPage />
              </MainLayout>
            </ProtectedRoute>
          }
        />

        {/* ðŸ†• Create Deployment Support (developer only) */}
        <Route
          path="/create-deployment-support"
          element={
            <ProtectedRoute isAuthenticated={isAuthenticated}>
              <MainLayout>
                <CreateDeploymentSupportPage />
              </MainLayout>
            </ProtectedRoute>
          }
        />

        {/* Halaman Kalender Deployment */}
        <Route
          path="/deployment-calendar"
          element={
            isAuthenticated ? (
              <MainLayout>
                <DeploymentCalendarPage />
              </MainLayout>
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        {/* Halaman Kalender Deployment Support (versi 2) */}
        <Route
          path="/deployment-calendar2"
          element={
            isAuthenticated ? (
              <MainLayout>
                <DeploymentCalendarPage2 />
              </MainLayout>
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        {/* Halaman Kalender Deployment Support (versi 3) */}
        <Route
          path="/deployment-calendar3"
          element={
            isAuthenticated ? (
              <MainLayout>
                <DeploymentCalendarPage3 />
              </MainLayout>
            ) : (
              <Navigate to="/login" />
            )
          }
        />


        <Route path="/" element={<Navigate to="/tasks" />} />
      </Routes>
    </Router>
  );
};

export default App;
