import React from "react";
import Navbar from "../components/Navbar";
import { Box, CssBaseline } from "@mui/material";

const MainLayout = ({ children }) => {
  return (
    <>
      {/* Global style */}
      <CssBaseline />

      <Box sx={{ display: "flex", minHeight: "100vh" }}>
        {/* Navbar */}
        <Navbar />

        {/* Main content */}
        <Box
          component="main"
          sx={{
            flexGrow: 1,
            p: 3,
            backgroundColor: "#f5f5f5", // Menambahkan latar belakang pada konten utama
            borderRadius: 2,
            boxShadow: 3,
            marginLeft: `${240}px`, // Menyesuaikan dengan lebar Navbar
          }}
        >
          {children}
        </Box>
      </Box>
    </>
  );
};

export default MainLayout;
