import React, { useEffect, useState } from "react";
import {
  Box,
  Typography,
  Button,
  TextField,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Pagination,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import api from "../services/api";

const KnowledgeCenter = () => {
  const [data, setData] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [search, setSearch] = useState("");
  const [openDialog, setOpenDialog] = useState(false);
  const [formData, setFormData] = useState({ problem: "", solution: "" });
  const [editId, setEditId] = useState(null);
  const [page, setPage] = useState(1);
  const rowsPerPage = 10;

  // Fetch data dari backend
  const fetchData = async () => {
    try {
      const res = await api.get("/knowledge-center");
      setData(res.data.data);
      setFiltered(res.data.data);
    } catch (err) {
      console.error("‚ùå Gagal ambil data:", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Live search
  useEffect(() => {
    const result = data.filter((item) =>
      item.problem.toLowerCase().includes(search.toLowerCase())
    );
    setFiltered(result);
    setPage(1);
  }, [search, data]);

  // Pagination
  const paginatedData = filtered.slice(
    (page - 1) * rowsPerPage,
    page * rowsPerPage
  );

  const handleChangePage = (event, value) => {
    setPage(value);
  };

  // Buka modal tambah/edit
  const handleOpenDialog = (item = null) => {
    if (item) {
      setEditId(item.id);
      setFormData({ problem: item.problem, solution: item.solution });
    } else {
      setEditId(null);
      setFormData({ problem: "", solution: "" });
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => setOpenDialog(false);

  // Tambah / Edit data
  const handleSubmit = async () => {
    try {
      if (!formData.problem || !formData.solution) {
        alert("Problem dan Solution wajib diisi!");
        return;
      }

      if (editId) {
        await api.put(`/knowledge-center/${editId}`, formData);
      } else {
        await api.post("/knowledge-center", formData);
      }

      setOpenDialog(false);
      fetchData();
    } catch (err) {
      console.error("‚ùå Gagal simpan data:", err);
    }
  };

  // Hapus data
  const handleDelete = async (id) => {
    if (!window.confirm("Yakin ingin menghapus data ini?")) return;
    try {
      await api.delete(`/knowledge-center/${id}`);
      fetchData();
    } catch (err) {
      console.error("‚ùå Gagal hapus data:", err);
    }
  };

  return (
    <Box sx={{ p: 4, maxWidth: 900, mx: "auto" }}>
      <Typography variant="h5" fontWeight="bold" mb={2}>
        üìö Knowledge Center
      </Typography>

      {/* üîç Search dan Tambah */}
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <TextField
          label="Cari Problem..."
          variant="outlined"
          size="small"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          sx={{ width: "60%" }}
        />
        <Button variant="contained" color="primary" onClick={() => handleOpenDialog()}>
          + Tambah Data
        </Button>
      </Box>

      {/* üìã Accordion FAQ */}
      {paginatedData.length > 0 ? (
        paginatedData.map((item) => (
          <Accordion key={item.id}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}>
              <Typography fontWeight="bold">{item.problem}</Typography>
            </AccordionSummary>
            <AccordionDetails>
              <Typography sx={{ whiteSpace: "pre-line" }}>
                {item.solution}
              </Typography>

              {/* Tombol Edit / Hapus */}
              <Box display="flex" gap={1} mt={2}>
                <Button
                  variant="outlined"
                  size="small"
                  onClick={() => handleOpenDialog(item)}
                >
                  Edit
                </Button>
                <Button
                  variant="outlined"
                  color="error"
                  size="small"
                  onClick={() => handleDelete(item.id)}
                >
                  Hapus
                </Button>
              </Box>
            </AccordionDetails>
          </Accordion>
        ))
      ) : (
        <Typography align="center" color="textSecondary">
          Tidak ada data ditemukan.
        </Typography>
      )}

      {/* üî¢ Pagination */}
      <Box mt={3} display="flex" justifyContent="center">
        <Pagination
          count={Math.ceil(filtered.length / rowsPerPage)}
          page={page}
          onChange={handleChangePage}
          color="primary"
        />
      </Box>

      {/* üí¨ Modal Form */}
      <Dialog open={openDialog} onClose={handleCloseDialog} fullWidth maxWidth="sm">
        <DialogTitle>
          {editId ? "Edit Knowledge" : "Tambah Knowledge"}
        </DialogTitle>
        <DialogContent>
          <TextField
            label="Problem"
            fullWidth
            margin="normal"
            value={formData.problem}
            onChange={(e) =>
              setFormData({ ...formData, problem: e.target.value })
            }
          />
          <TextField
            label="Solution"
            fullWidth
            multiline
            rows={4}
            margin="normal"
            value={formData.solution}
            onChange={(e) =>
              setFormData({ ...formData, solution: e.target.value })
            }
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Batal</Button>
          <Button variant="contained" onClick={handleSubmit}>
            Simpan
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default KnowledgeCenter;
