import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Paper,
  CircularProgress,
  Card,
  CardContent,
  Chip,
  IconButton,
} from "@mui/material";
import { ArrowBack, ArrowForward } from "@mui/icons-material";

// ðŸ”¹ Format tanggal
const formatDate = (date) =>
  new Date(date).toLocaleDateString("id-ID", {
    weekday: "short",
    day: "2-digit",
    month: "short",
  });

// ðŸ”¹ Ambil Senin dari minggu tertentu
const getWeekStart = (date) => {
  const d = new Date(date);
  const day = d.getDay();
  const diff = d.getDate() - day + (day === 0 ? -6 : 1);
  return new Date(d.setDate(diff));
};

// ðŸ”¹ Buat array tanggal 7 hari (Seninâ€“Minggu)
const getWeekDates = (startDate) => {
  const days = [];
  const start = new Date(startDate);
  for (let i = 0; i < 7; i++) {
    const d = new Date(start);
    d.setDate(start.getDate() + i);
    days.push(d);
  }
  return days;
};

const DeploymentBoardPage = () => {
  const [deploymentRequests, setDeploymentRequests] = useState([]);
  const [deploymentSupports, setDeploymentSupports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentWeek, setCurrentWeek] = useState(getWeekStart(new Date())); // minggu aktif

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const startDate = new Date().toISOString().split("T")[0];
      const endDate = new Date(new Date().setMonth(new Date().getMonth() + 1))
        .toISOString()
        .split("T")[0];

      try {
        const [reqRes, supRes] = await Promise.all([
          api.get("/deployment-requests", { params: { startDate, endDate } }),
          api.get("/deployment-supports", { params: { startDate, endDate } }),
        ]);

        setDeploymentRequests(reqRes.data?.data || []);
        setDeploymentSupports(supRes.data?.data || []);
      } catch (err) {
        console.error("âŒ Gagal mengambil data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // ðŸ”¹ Warna berdasarkan risiko / jenis event
  const riskColor = (event) => {
    if (event.type === "support") return "#90caf9";
    switch (event.riskImpact) {
      case "Low":
        return "#A5D6A7";
      case "Medium":
        return "#FFF59D";
      case "High":
        return "#EF9A9A";
      case "Major Release":
        return "#3a3a3a";
      default:
        return "#E0E0E0";
    }
  };

  // ðŸ”¹ Gabungkan request & support
  const allEvents = [
    ...deploymentRequests.map((r) => ({ ...r, type: "request", date: r.implementDate })),
    ...deploymentSupports.map((s) => ({ ...s, type: "support", date: s.implementDate })),
  ];

  // ðŸ”¹ Ambil semua event di minggu aktif
  const weekDates = getWeekDates(currentWeek);
  const eventsByDay = {};
  weekDates.forEach((day) => {
    const key = day.toISOString().split("T")[0];
    eventsByDay[key] = allEvents.filter(
      (e) => e.date && e.date.startsWith(key)
    );
  });

  // ðŸ”¹ Pagination: minggu sebelumnya / berikutnya
  const goToPrevWeek = () => {
    const prev = new Date(currentWeek);
    prev.setDate(prev.getDate() - 7);
    setCurrentWeek(prev);
  };

  const goToNextWeek = () => {
    const next = new Date(currentWeek);
    next.setDate(next.getDate() + 7);
    setCurrentWeek(next);
  };

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
        <CircularProgress />
      </Box>
    );
  }

  const weekEnd = new Date(currentWeek);
  weekEnd.setDate(weekEnd.getDate() + 6);

  return (
    <>
      {/* Judul dan navigasi minggu */}
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", mb: 2 }}>
        <IconButton onClick={goToPrevWeek}>
          <ArrowBack />
        </IconButton>
        <Typography variant="h5" fontWeight="bold" sx={{ color: "#1976d2", mx: 2, textAlign: "center" }}>
          Minggu {currentWeek.toLocaleDateString("id-ID", { day: "2-digit", month: "short" })} -{" "}
          {weekEnd.toLocaleDateString("id-ID", { day: "2-digit", month: "short", year: "numeric" })}
        </Typography>
        <IconButton onClick={goToNextWeek}>
          <ArrowForward />
        </IconButton>
      </Box>

      {/* Layout 2 baris */}
      <Box sx={{ display: "grid", gridTemplateRows: "1fr 1fr", gap: 2 }}>
        {/* ðŸ”¹ Baris atas: Seninâ€“Kamis */}
        <Box sx={{ display: "grid", gridTemplateColumns: "repeat(4, 1fr)", gap: 2 }}>
          {weekDates.slice(0, 4).map((day) => {
            const key = day.toISOString().split("T")[0];
            const events = eventsByDay[key] || [];
            return (
              <Paper key={key} sx={{ p: 1.5, borderRadius: 2, minHeight: 200 }}>
                <Typography variant="subtitle1" fontWeight="bold" sx={{ color: "#1976d2", mb: 1 }}>
                  {formatDate(day)}
                </Typography>
                {events.length > 0 ? (
                  events.map((event) => (
                    <Card
                      key={`${event.type}-${event.id}`}
                      sx={{
                        backgroundColor: riskColor(event),
                        mb: 1,
                        "&:hover": { opacity: 0.85 },
                      }}
                    >
                      <CardContent sx={{ p: 1 }}>
                        <Typography variant="body2" fontWeight="bold" noWrap>
                          {event.title}
                        </Typography>
                        <Chip
                          label={event.type === "request" ? "Request" : "Support"}
                          size="small"
                          sx={{ mt: 0.5 }}
                        />
                      </CardContent>
                    </Card>
                  ))
                ) : (
                  <Typography variant="body2" sx={{ color: "#888" }}>
                    Tidak ada event
                  </Typography>
                )}
              </Paper>
            );
          })}
        </Box>

        {/* ðŸ”¹ Baris bawah: Jumatâ€“Minggu */}
        <Box sx={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: 2 }}>
          {weekDates.slice(4).map((day) => {
            const key = day.toISOString().split("T")[0];
            const events = eventsByDay[key] || [];
            return (
              <Paper key={key} sx={{ p: 1.5, borderRadius: 2, minHeight: 200 }}>
                <Typography variant="subtitle1" fontWeight="bold" sx={{ color: "#1976d2", mb: 1 }}>
                  {formatDate(day)}
                </Typography>
                {events.length > 0 ? (
                  events.map((event) => (
                    <Card
                      key={`${event.type}-${event.id}`}
                      sx={{
                        backgroundColor: riskColor(event),
                        mb: 1,
                        "&:hover": { opacity: 0.85 },
                      }}
                    >
                      <CardContent sx={{ p: 1 }}>
                        <Typography variant="body2" fontWeight="bold" noWrap>
                          {event.title}
                        </Typography>
                        <Chip
                          label={event.type === "request" ? "Request" : "Support"}
                          size="small"
                          sx={{ mt: 0.5 }}
                        />
                      </CardContent>
                    </Card>
                  ))
                ) : (
                  <Typography variant="body2" sx={{ color: "#888" }}>
                    Tidak ada event
                  </Typography>
                )}
              </Paper>
            );
          })}
        </Box>
      </Box>
    </>
  );
};

export default DeploymentBoardPage;
