import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  CircularProgress,
  Box,
} from "@mui/material";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const res = await api.get("/tasks"); // pakai baseURL dari api.js
        setTasks(res.data.data || []);
      } catch (err) {
        console.error("❌ Gagal mengambil data task:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchTasks();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", mt: 3 }}>
        <CircularProgress size={50} />
      </Box>
    );
  }

  return (
    <TableContainer component={Paper} sx={{ mt: 3, borderRadius: 2, boxShadow: 3 }}>
      <Table>
        <TableHead>
          <TableRow sx={{ backgroundColor: "#2575fc", color: "#fff" }}>
            <TableCell sx={{ fontWeight: "bold", color: "#fff" }}>Judul</TableCell>
            <TableCell sx={{ fontWeight: "bold", color: "#fff" }}>Deskripsi</TableCell>
            <TableCell sx={{ fontWeight: "bold", color: "#fff" }}>Bentuk Support</TableCell>
            <TableCell sx={{ fontWeight: "bold", color: "#fff" }}>Aplikasi</TableCell>
            <TableCell sx={{ fontWeight: "bold", color: "#fff" }}>PIC SQI</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {tasks.length > 0 ? (
            tasks.map((task) => (
              <TableRow
                key={task.id}
                sx={{
                  "&:hover": {
                    backgroundColor: "rgba(0, 0, 0, 0.05)",
                  },
                }}
              >
                <TableCell>{task.title}</TableCell>
                <TableCell>{task.description}</TableCell>
                <TableCell>
                  {task.supportType
                    ? task.supportType.name
                    : task.customSupportType || "—"}
                </TableCell>
                <TableCell>{task.application?.name || "—"}</TableCell>
                <TableCell>{task.sqiPic?.name || "—"}</TableCell>
              </TableRow>
            ))
          ) : (
            <TableRow>
              <TableCell colSpan={5} align="center" sx={{ color: "#757575" }}>
                Belum ada task.
              </TableCell>
            </TableRow>
          )}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TaskList;
