import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  CircularProgress,
  Paper,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
} from "@mui/material";
import DownloadIcon from "@mui/icons-material/Download";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";

const DeploymentCalendarPage3 = () => {
  const [deploymentRequests, setDeploymentRequests] = useState([]);
  const [deploymentSupports, setDeploymentSupports] = useState([]);
  const [loading, setLoading] = useState(true);

  const [selectedEvent, setSelectedEvent] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedStatus, setSelectedStatus] = useState("");
  const [selectedPic, setSelectedPic] = useState("");
  const [sqiPics, setSqiPics] = useState([]);
  const [isUpdating, setIsUpdating] = useState(false);

  const statusesDeployment = ["null", "success", "redeploy", "cancel"];
  const statusesSupport = ["null", "Low", "Medium", "High"];
  const role = "SQI";

  const fetchDeploymentRequests = async () => {
    try {
      const res = await api.get("/deployment-requests", {
        params: {
          startDate: new Date().toISOString().split("T")[0],
          endDate: new Date(new Date().setMonth(new Date().getMonth() + 1))
            .toISOString()
            .split("T")[0],
        },
      });
      setDeploymentRequests(res.data?.data || res.data || []);
    } catch (err) {
      console.error("❌ Gagal mengambil deployment requests:", err);
      setDeploymentRequests([]);
    }
  };

  const fetchDeploymentSupports = async () => {
    try {
      const res = await api.get("/deployment-supports", {
        params: {
          startDate: new Date().toISOString().split("T")[0],
          endDate: new Date(new Date().setMonth(new Date().getMonth() + 1))
            .toISOString()
            .split("T")[0],
        },
      });
      setDeploymentSupports(res.data?.data || res.data || []);
    } catch (err) {
      console.error("❌ Gagal mengambil deployment supports:", err);
      setDeploymentSupports([]);
    }
  };

  const fetchSqiPics = async () => {
    try {
      const res = await api.get("/sqi-pics");
      setSqiPics(res.data?.data || res.data || []);
    } catch (err) {
      console.error("❌ Gagal mengambil SQI PICs:", err);
    }
  };

  useEffect(() => {
    const fetchAll = async () => {
      setLoading(true);
      await Promise.all([fetchDeploymentRequests(), fetchDeploymentSupports()]);
      if (role === "SQI") await fetchSqiPics();
      setLoading(false);
    };
    fetchAll();
  }, []);

  const handleEventClick = (info) => {
    const { id, title, extendedProps } = info.event;
    setSelectedEvent({
      type: extendedProps.type, // "request" atau "support"
      id,
      title,
      implementDate: extendedProps.implementDate,
      applicationName: extendedProps.applicationName || extendedProps.application,
      riskImpact: extendedProps.riskImpact,
      attachment: extendedProps.attachment,
      status: extendedProps.status || "null",
      sqiPicId: extendedProps.sqiPicId || null,
    });
    setSelectedStatus(extendedProps.status || "null");
    setSelectedPic(extendedProps.sqiPicId || "");
    setOpenDialog(true);
  };

  const handleDownload = async () => {
    try {
      if (!selectedEvent?.attachment) return;
      const filename = selectedEvent.attachment.split("/").pop();
      const urlPath =
        selectedEvent.type === "request"
          ? `/deployment-requests/download/${filename}`
          : `/deployment-supports/download/${filename}`;
      const response = await api.get(urlPath, { responseType: "blob" });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", filename);
      document.body.appendChild(link);
      link.click();
      link.parentNode.removeChild(link);
    } catch (err) {
      console.error("❌ Gagal mengunduh file:", err);
      alert("Gagal mengunduh file.");
    }
  };

  const handleStatusChange = async (event) => {
    if (isUpdating) return;
    setIsUpdating(true);
    const newStatus = event.target.value;
    setSelectedStatus(newStatus);

    try {
      const apiPath =
        selectedEvent.type === "request"
          ? `/deployment-requests/${selectedEvent.id}`
          : `/deployment-supports/${selectedEvent.id}`;
      await api.patch(apiPath, { status: newStatus === "null" ? null : newStatus });

      if (selectedEvent.type === "request") await fetchDeploymentRequests();
      else await fetchDeploymentSupports();

      setSelectedEvent((prev) => ({
        ...prev,
        status: newStatus === "null" ? null : newStatus,
      }));

      alert("Status berhasil diperbarui!");
    } catch (err) {
      console.error("❌ Gagal memperbarui status:", err);
      alert("Gagal memperbarui status.");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleAssignPic = async (event) => {
    if (isUpdating) return;
    setIsUpdating(true);
    const picId = event.target.value;
    setSelectedPic(picId);

    try {
      await api.patch(`/deployment-requests/${selectedEvent.id}`, {
        sqiPicId: picId === "" ? null : picId,
      });
      await fetchDeploymentRequests();
      setSelectedEvent((prev) => ({
        ...prev,
        sqiPicId: picId === "" ? null : picId,
      }));
      alert("PIC berhasil di-assign!");
    } catch (err) {
      console.error("❌ Gagal assign PIC:", err);
      alert("Gagal assign PIC.");
    } finally {
      setIsUpdating(false);
    }
  };

  if (loading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
          marginLeft: "220px",
        }}
      >
        <CircularProgress size={50} />
      </Box>
    );
  }

  // Gabungkan semua event
  const calendarEvents = [
    ...deploymentRequests.map((r) => ({
      ...r,
      type: "request",
      title: `${r.title} - ${r.riskImpact}`,
      date: r.implementDate,
      applicationName: r.application?.name || "Unknown",
      backgroundColor:
        r.riskImpact === "Low"
          ? "#A5D6A7"
          : r.riskImpact === "Medium"
          ? "#FFF59D"
          : r.riskImpact === "High"
          ? "#EF9A9A"
          : r.riskImpact === "Major Release"
          ? "#3a3a3aff"
          : "#E0E0E0",
      textColor: r.riskImpact === "Major Release" ? "#FFFFFF" : "#000000",
    })),
    ...deploymentSupports.map((s) => ({
      ...s,
      type: "support",
      title: `${s.title} - ${s.riskImpact}`,
      date: s.implementDate,
      applicationName: s.application || "Unknown",
      backgroundColor:
        s.riskImpact === "Low"
          ? "#A5D6A7"
          : s.riskImpact === "Medium"
          ? "#FFF59D"
          : s.riskImpact === "High"
          ? "#EF9A9A"
          : "#E0E0E0",
      textColor: "#000000",
    })),
  ];

  return (
    <Box sx={{ display: "flex" }}>
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          marginLeft: "220px",
          minHeight: "100vh",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Typography variant="h5" fontWeight="bold" sx={{ color: "#1976d2", mb: 3 }}>
          Kalender Deployment (Requests & Supports)
        </Typography>

        <Paper elevation={3} sx={{ width: "90%", maxWidth: 900, p: 2, borderRadius: 2 }}>
          <FullCalendar
            plugins={[dayGridPlugin]}
            initialView="dayGridMonth"
            height="80vh"
            events={calendarEvents}
            eventDisplay="block"
            eventContent={(arg) => (
              <div style={{ color: arg.event.extendedProps.textColor }}>
                {arg.event.title}
              </div>
            )}
            eventBorderColor="#00000030"
            displayEventTime={false}
            eventClick={handleEventClick}
            eventDidMount={(info) => {
              info.el.style.padding = "4px 6px";
              info.el.style.borderRadius = "4px";
              info.el.style.fontWeight = "600";
              info.el.style.textAlign = "center";
            }}
          />
        </Paper>

        {/* Dialog Detail */}
        <Dialog open={openDialog} onClose={() => setOpenDialog(false)} fullWidth>
          <DialogTitle>
            Detail {selectedEvent?.type === "request" ? "Deployment Request" : "Deployment Support"}
          </DialogTitle>
          <DialogContent dividers>
            {selectedEvent ? (
              <Box sx={{ display: "flex", flexDirection: "column", gap: 1 }}>
                <Typography>
                  <strong>Judul:</strong> {selectedEvent.title}
                </Typography>
                <Typography>
                  <strong>Aplikasi:</strong> {selectedEvent.applicationName}
                </Typography>
                <Typography>
                  <strong>Tanggal Implementasi:</strong> {selectedEvent.implementDate}
                </Typography>
                <Typography>
                  <strong>Risk Impact:</strong> {selectedEvent.riskImpact}
                </Typography>

                {role === "SQI" && selectedEvent.type === "request" && (
                  <FormControl size="small" sx={{ mt: 1, width: "100%" }}>
                    <InputLabel id="sqi-pic-label">Assign SQI PIC</InputLabel>
                    <Select
                      labelId="sqi-pic-label"
                      value={selectedPic}
                      onChange={handleAssignPic}
                      label="Assign SQI PIC"
                    >
                      {sqiPics.map((pic) => (
                        <MenuItem key={pic.id} value={pic.id}>
                          {pic.name}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                )}
              </Box>
            ) : (
              <Typography>Tidak ada data.</Typography>
            )}
          </DialogContent>

          <DialogActions>
            {selectedEvent?.attachment && (
              <Button
                variant="outlined"
                color="primary"
                size="small"
                startIcon={<DownloadIcon />}
                onClick={handleDownload}
                sx={{ textTransform: "none", fontWeight: 500 }}
              >
                Download
              </Button>
            )}

            {role === "SQI" && (
              <FormControl size="small" sx={{ minWidth: 120, mr: 2 }}>
                <InputLabel id="status-label">Status</InputLabel>
                <Select
                  labelId="status-label"
                  value={selectedStatus}
                  onChange={handleStatusChange}
                  label="Status"
                >
                  {(selectedEvent?.type === "request" ? statusesDeployment : statusesSupport).map(
                    (status) => (
                      <MenuItem key={status} value={status}>
                        {status === "null" ? "-" : status}
                      </MenuItem>
                    )
                  )}
                </Select>
              </FormControl>
            )}

            <Button onClick={() => setOpenDialog(false)} color="primary">
              Tutup
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Box>
  );
};

export default DeploymentCalendarPage3;
