import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, TextField, Button, MenuItem, Typography } from "@mui/material";
import { useNavigate } from "react-router-dom";

const CreateTaskPage = () => {
  const [applications, setApplications] = useState([]);
  const [sqiPics, setSqiPics] = useState([]);
  const [form, setForm] = useState({
    title: "",
    description: "",
    applicationId: "",
    sqiPicId: "",
  });

  const navigate = useNavigate();

  // Ambil data dari database
  useEffect(() => {
    const fetchData = async () => {
      const [appRes, picRes] = await Promise.all([
        axios.get("http://localhost:4000/api/applications"),
        axios.get("http://localhost:5000/api/sqi-pics"),
      ]);
      setApplications(appRes.data);
      setSqiPics(picRes.data);
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:5000/api/tasks", form);
    navigate("/"); // kembali ke halaman utama
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 5 }}>
      <Typography variant="h5" gutterBottom>
        Tambah Task Baru
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          fullWidth
          label="Judul Task"
          name="title"
          value={form.title}
          onChange={handleChange}
          margin="normal"
          required
        />
        <TextField
          fullWidth
          label="Deskripsi"
          name="description"
          value={form.description}
          onChange={handleChange}
          margin="normal"
          multiline
          rows={3}
          required
        />
        <TextField
          select
          fullWidth
          label="Aplikasi"
          name="applicationId"
          value={form.applicationId}
          onChange={handleChange}
          margin="normal"
          required
        >
          {applications.map((app) => (
            <MenuItem key={app.id} value={app.id}>
              {app.name}
            </MenuItem>
          ))}
        </TextField>
        <TextField
          select
          fullWidth
          label="PIC SQI"
          name="sqiPicId"
          value={form.sqiPicId}
          onChange={handleChange}
          margin="normal"
          required
        >
          {sqiPics.map((pic) => (
            <MenuItem key={pic.id} value={pic.id}>
              {pic.name}
            </MenuItem>
          ))}
        </TextField>

        <Button type="submit" variant="contained" color="primary" sx={{ mt: 2 }}>
          Simpan Task
        </Button>
      </form>
    </Container>
  );
};

export default CreateTaskPage;
