import React from "react";
import Navbar from "../components/Navbar";
import { Box, CssBaseline } from "@mui/material";

const MainLayout = ({ children }) => {
  return (
    <>
      <CssBaseline />
      <Box sx={{ display: "flex", minHeight: "100vh" }}>
        <Navbar /> {/* Navbar tetap di kiri */}

        <Box
          component="main"
          sx={{
            flexGrow: 1,  // Konten utama mengisi ruang
            p: 3,
            backgroundColor: "#f5f5f5",
            borderRadius: 2,
            boxShadow: 3,
            marginLeft: 240, // Sesuaikan dengan lebar Navbar
            overflowY: "auto", // Pastikan scroll hanya vertikal jika dibutuhkan
          }}
        >
          {children} {/* Render children, yaitu konten utama */}
        </Box>
      </Box>
    </>
  );
};

export default MainLayout;
