import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Paper,
  CircularProgress,
  Card,
  CardContent,
  Pagination,
  Grid,
} from "@mui/material";

// üîπ Format tanggal tampilan (Senin, 11 Nov)
const formatDate = (date) =>
  new Date(date).toLocaleDateString("id-ID", {
    weekday: "long",
    day: "2-digit",
    month: "short",
  });

// üîπ Hitung awal minggu (Senin)
const getWeekStart = (date) => {
  if (!date || isNaN(new Date(date))) return new Date();
  const d = new Date(date);
  const day = d.getDay();
  const diff = d.getDate() - day + (day === 0 ? -6 : 1);
  return new Date(d.setDate(diff));
};

const DeploymentBoardPage = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentWeek, setCurrentWeek] = useState(0);

  useEffect(() => {
  const fetchCalendar = async () => {
    setLoading(true);
    try {
      const res = await api.get("/calendar");

      // ‚úÖ Ambil array data dengan aman
      const data = Array.isArray(res.data)
        ? res.data
        : Array.isArray(res.data.data)
        ? res.data.data
        : [];

      console.log("‚úÖ Data yang dipakai frontend:", data);

      // ‚úÖ Pastikan setiap item punya tanggal valid
      const cleaned = data
        .map((item) => {
          const dateField =
            item.implementDate ||
            item.supportDate ||
            item.date ||
            item.createdAt ||
            item.updatedAt;
          const parsedDate = new Date(dateField);
          return {
            ...item,
            date: parsedDate instanceof Date && !isNaN(parsedDate)
              ? parsedDate
              : null,
          };
        })
        .filter((e) => e.date !== null);

      setEvents(cleaned);
    } catch (err) {
      console.error("‚ùå Gagal mengambil data:", err);
    } finally {
      setLoading(false);
    }
  };


    fetchCalendar();
  }, []);

  if (loading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  // üîπ Kelompokkan berdasarkan minggu dan hari
  const eventsByWeek = {};
  events.forEach((event) => {
    const weekStart = getWeekStart(event.date).toISOString().split("T")[0];
    if (!eventsByWeek[weekStart]) eventsByWeek[weekStart] = {};
    const dateStr = event.date.toISOString().split("T")[0];
    if (!eventsByWeek[weekStart][dateStr])
      eventsByWeek[weekStart][dateStr] = [];
    eventsByWeek[weekStart][dateStr].push(event);
  });

  // üîπ Urutkan minggu
  const weekKeys = Object.keys(eventsByWeek).sort();
  const currentWeekKey = weekKeys[currentWeek] || weekKeys[0];
  const weekDays = Array.from({ length: 7 }, (_, i) => {
    const d = new Date(currentWeekKey);
    d.setDate(d.getDate() + i);
    return d;
  });

  const riskColor = (event) => {
    if (event.type === "support") return "#90caf9";
    switch (event.riskImpact) {
      case "Low":
        return "#A5D6A7";
      case "Medium":
        return "#FFF59D";
      case "High":
        return "#EF9A9A";
      case "Major Release":
        return "#3a3a3a";
      default:
        return "#E0E0E0";
    }
  };

  return (
    <Box sx={{ px: 2, pb: 5 }}>
      <Typography
        variant="h5"
        fontWeight="bold"
        sx={{ color: "#1976d2", mb: 3, textAlign: "center" }}
      >
        Deployment Board (Per Minggu)
      </Typography>

      {/* üîπ Layout 2 baris (4 hari atas, 3 hari bawah) */}
      {[0, 4].map((startIdx, rowIndex) => (
        <Box
          key={rowIndex}
          sx={{
            display: "flex",
            justifyContent: "center",
            flexWrap: "wrap",
            gap: 2,
            mb: 3,
          }}
        >
          {weekDays.slice(startIdx, startIdx + (rowIndex === 0 ? 4 : 3)).map((day) => {
            const dateStr = day.toISOString().split("T")[0];
            const eventsToday = eventsByWeek[currentWeekKey]?.[dateStr] || [];
            const requests = eventsToday.filter(
              (e) => e.type === "request" || e.source === "request"
            );
            const supports = eventsToday.filter(
              (e) => e.type === "support" || e.source === "support"
            );

            return (
              <Paper
                key={dateStr}
                sx={{
                  p: 2,
                  width: 280,
                  flexShrink: 0,
                  backgroundColor: "#f9f9f9",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                  textAlign: "center",
                }}
              >
                <Typography
                  variant="subtitle1"
                  fontWeight="bold"
                  sx={{ mb: 1, color: "#1976d2" }}
                >
                  {formatDate(day)}
                </Typography>

                <Grid container spacing={1}>
                  {/* üîπ Kolom kiri - Request */}
                  <Grid
                    item
                    xs={6}
                    sx={{
                      pr: 1,
                      borderRight: "1px solid #ddd",
                      display: "flex",
                      flexDirection: "column",
                      alignItems: "center",
                    }}
                  >
                    <Typography
                      variant="caption"
                      sx={{ fontWeight: "bold", display: "block", mb: 0.5 }}
                    >
                      Request
                    </Typography>
                    {requests.length === 0 ? (
                      <Typography variant="caption" sx={{ opacity: 0.6 }}>
                        (Kosong)
                      </Typography>
                    ) : (
                      requests.map((event) => (
                        <Card
                          key={event.id}
                          sx={{
                            mb: 1,
                            backgroundColor: riskColor(event),
                            cursor: "pointer",
                          }}
                        >
                          <CardContent
                            sx={{
                              px: 1,
                              py: 0,
                              "&:last-child": { pb: 0 },
                              height: 48,
                              display: "flex",
                              alignItems: "center",
                              justifyContent: "center",
                              textAlign: "center",
                            }}
                          >
                            <Typography
                              variant="body2"
                              fontWeight="bold"
                              noWrap
                              sx={{ width: "100%" }}
                            >
                              {event.title || event.requestTitle}
                            </Typography>
                          </CardContent>
                        </Card>
                      ))
                    )}
                  </Grid>

                  {/* üîπ Kolom kanan - Support */}
                  <Grid
                    item
                    xs={6}
                    sx={{
                      pl: 1,
                      display: "flex",
                      flexDirection: "column",
                      alignItems: "center",
                    }}
                  >
                    <Typography
                      variant="caption"
                      sx={{ fontWeight: "bold", display: "block", mb: 0.5 }}
                    >
                      Support
                    </Typography>
                    {supports.length === 0 ? (
                      <Typography variant="caption" sx={{ opacity: 0.6 }}>
                        (Kosong)
                      </Typography>
                    ) : (
                      supports.map((event) => (
                        <Card
                          key={event.id}
                          sx={{
                            mb: 1,
                            backgroundColor: riskColor(event),
                            cursor: "pointer",
                          }}
                        >
                          <CardContent
                            sx={{
                              px: 1,
                              py: 0,
                              "&:last-child": { pb: 0 },
                              height: 48,
                              display: "flex",
                              alignItems: "center",
                              justifyContent: "center",
                              textAlign: "center",
                            }}
                          >
                            <Typography
                              variant="body2"
                              fontWeight="bold"
                              noWrap
                              sx={{ width: "100%" }}
                            >
                              {event.title || event.supportTitle}
                            </Typography>
                          </CardContent>
                        </Card>
                      ))
                    )}
                  </Grid>
                </Grid>
              </Paper>
            );
          })}
        </Box>
      ))}

      {/* üîπ Pagination untuk minggu */}
      <Box sx={{ display: "flex", justifyContent: "center", mt: 2 }}>
        <Pagination
          count={weekKeys.length}
          page={currentWeek + 1}
          onChange={(e, val) => setCurrentWeek(val - 1)}
          color="primary"
        />
      </Box>
    </Box>
  );
};

export default DeploymentBoardPage;
