import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  CircularProgress,
  Paper,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
} from "@mui/material";
import DownloadIcon from "@mui/icons-material/Download";

const DeploymentCalendarPage = () => {
  const [deploymentRequests, setDeploymentRequests] = useState([]);
  const [deploymentSupports, setDeploymentSupports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [statuses] = useState(["null", "success", "redeploy", "cancel"]);
  const [sqiPics, setSqiPics] = useState([]);
  const [selectedStatus, setSelectedStatus] = useState("");
  const [selectedPic, setSelectedPic] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);

  const role = "SQI";

  const handleCardClick = (item, type) => {
    setSelectedEvent({ ...item, type });
    setSelectedStatus(item.status || "null");
    setSelectedPic(item.sqiPicId || "");
    setOpenDialog(true);
  };

  const fetchDeploymentRequests = async () => {
    try {
      const res = await api.get("/deployment-requests");
      setDeploymentRequests(res.data?.data || res.data || []);
    } catch (err) {
      console.error("‚ùå Gagal mengambil deployment requests:", err);
      setDeploymentRequests([]);
    }
  };

  const fetchDeploymentSupports = async () => {
    try {
      const res = await api.get("/deployment-supports");
      setDeploymentSupports(res.data?.data || res.data || []);
    } catch (err) {
      console.error("‚ùå Gagal mengambil deployment supports:", err);
      setDeploymentSupports([]);
    }
  };

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      await Promise.all([fetchDeploymentRequests(), fetchDeploymentSupports()]);
      setLoading(false);
    };

    const fetchSqiPics = async () => {
      try {
        const res = await api.get("/sqi-pics");
        setSqiPics(res.data?.data || res.data || []);
      } catch (err) {
        console.error("‚ùå Gagal mengambil data SQI PIC:", err);
      }
    };

    loadData();
    if (role === "SQI") fetchSqiPics();
  }, []);

  const handleDownload = async () => {
    try {
      if (!selectedEvent?.attachment) return;
      const filename = selectedEvent.attachment.split("/").pop();
      const response = await api.get(
        `/deployment-${selectedEvent.type === "support" ? "supports" : "requests"}/download/${filename}`,
        { responseType: "blob" }
      );
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error("‚ùå Gagal mengunduh file:", err);
      alert("Gagal mengunduh file.");
    }
  };

  const handleStatusChange = async (event) => {
    if (isUpdating) return;
    setIsUpdating(true);
    const newStatus = event.target.value;
    setSelectedStatus(newStatus);

    try {
      const endpoint =
        selectedEvent.type === "support"
          ? `/deployment-supports/${selectedEvent.id}`
          : `/deployment-requests/${selectedEvent.id}`;

      await api.patch(endpoint, {
        status: newStatus === "null" ? null : newStatus,
      });
      await Promise.all([fetchDeploymentRequests(), fetchDeploymentSupports()]);
      alert("Status berhasil diperbarui!");
    } catch (err) {
      console.error("‚ùå Gagal memperbarui status:", err);
      alert("Gagal memperbarui status.");
    } finally {
      setIsUpdating(false);
    }
  };

  const handleAssignPic = async (event) => {
    if (isUpdating) return;
    setIsUpdating(true);
    const picId = event.target.value;
    setSelectedPic(picId);

    try {
      const endpoint =
        selectedEvent.type === "support"
          ? `/deployment-supports/${selectedEvent.id}`
          : `/deployment-requests/${selectedEvent.id}`;

      await api.patch(endpoint, {
        sqiPicId: picId === "" ? null : picId,
      });
      await Promise.all([fetchDeploymentRequests(), fetchDeploymentSupports()]);
      alert("PIC berhasil di-assign!");
    } catch (err) {
      console.error("‚ùå Gagal assign PIC:", err);
      alert("Gagal assign PIC.");
    } finally {
      setIsUpdating(false);
    }
  };

  const getColor = (riskImpact) => {
    switch (riskImpact) {
      case "Low":
        return "#A5D6A7";
      case "Medium":
        return "#FFF59D";
      case "High":
        return "#EF9A9A";
      case "Major Release":
        return "#3a3a3aff";
      default:
        return "#E0E0E0";
    }
  };

  // üîÑ Group per hari
  const days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
  const groupedData = days.map((day) => ({
    day,
    requests: deploymentRequests.filter(
      (r) =>
        new Date(r.implementDate).toLocaleDateString("en-US", { weekday: "long" }) === day
    ),
    supports: deploymentSupports.filter(
      (s) =>
        new Date(s.implementDate).toLocaleDateString("en-US", { weekday: "long" }) === day
    ),
  }));

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh", marginLeft: "220px" }}>
        <CircularProgress size={50} />
      </Box>
    );
  }

  return (
    <Box sx={{ display: "flex", overflowX: "auto", height: "100vh", p: 3, ml: "220px", gap: 2 }}>
      {groupedData.map(({ day, requests, supports }) => (
        <Paper
          key={day}
          elevation={4}
          sx={{
            minWidth: 400,
            flexShrink: 0,
            borderRadius: 2,
            display: "flex",
            flexDirection: "column",
            p: 2,
            backgroundColor: "#f8f9fa",
          }}
        >
          <Typography variant="h6" align="center" sx={{ mb: 2, color: "#1976d2", fontWeight: "bold" }}>
            {day}
          </Typography>

          <Box sx={{ display: "flex", gap: 1 }}>
            {/* ‚¨ÖÔ∏è Deployment Requests */}
            <Box sx={{ flex: 1 }}>
              <Typography variant="subtitle2" align="center" sx={{ mb: 1, color: "#2e7d32" }}>
                Deployment Request
              </Typography>
              {requests.length > 0 ? (
                requests.map((req) => (
                  <Paper
                    key={req.id}
                    sx={{
                      backgroundColor: getColor(req.riskImpact),
                      p: 1,
                      mb: 1,
                      borderRadius: 1.5,
                      cursor: "pointer",
                      fontSize: 13,
                      fontWeight: 600,
                      textAlign: "center",
                      color: req.riskImpact === "Major Release" ? "#fff" : "#000",
                      boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
                      "&:hover": { transform: "scale(1.03)" },
                      transition: "0.15s",
                    }}
                    onClick={() => handleCardClick(req, "request")}
                  >
                    {req.title}
                  </Paper>
                ))
              ) : (
                <Typography variant="body2" align="center" sx={{ color: "#9e9e9e" }}>
                  No requests
                </Typography>
              )}
            </Box>

            {/* ‚û°Ô∏è Deployment Supports */}
            <Box sx={{ flex: 1 }}>
              <Typography variant="subtitle2" align="center" sx={{ mb: 1, color: "#1565c0" }}>
                Deployment Support
              </Typography>
              {supports.length > 0 ? (
                supports.map((sup) => (
                  <Paper
                    key={sup.id}
                    sx={{
                      backgroundColor: "#e3f2fd",
                      p: 1,
                      mb: 1,
                      borderRadius: 1.5,
                      cursor: "pointer",
                      fontSize: 13,
                      fontWeight: 600,
                      textAlign: "center",
                      color: "#000",
                      boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
                      "&:hover": { transform: "scale(1.03)" },
                      transition: "0.15s",
                    }}
                    onClick={() => handleCardClick(sup, "support")}
                  >
                    {sup.supportTitle}
                  </Paper>
                ))
              ) : (
                <Typography variant="body2" align="center" sx={{ color: "#9e9e9e" }}>
                  No supports
                </Typography>
              )}
            </Box>
          </Box>
        </Paper>
      ))}

      {/* üîπ Dialog detail (tetap sama) */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)} fullWidth>
        <DialogTitle>
          {selectedEvent?.type === "support" ? "Detail Deployment Support" : "Detail Deployment Request"}
        </DialogTitle>
        <DialogContent dividers>
          {selectedEvent ? (
            <Box sx={{ display: "flex", flexDirection: "column", gap: 1 }}>
              <Typography><strong>Judul:</strong> {selectedEvent.title || selectedEvent.supportTitle}</Typography>
              <Typography><strong>Tanggal Implementasi:</strong> {selectedEvent.implementDate}</Typography>
              <Typography><strong>Risk Impact:</strong> {selectedEvent.riskImpact || "-"}</Typography>

              {role === "SQI" && (
                <FormControl size="small" sx={{ mt: 1, width: "100%" }}>
                  <InputLabel id="sqi-pic-label">Assign SQI PIC</InputLabel>
                  <Select
                    labelId="sqi-pic-label"
                    value={selectedPic}
                    onChange={handleAssignPic}
                    label="Assign SQI PIC"
                  >
                    {sqiPics.map((pic) => (
                      <MenuItem key={pic.id} value={pic.id}>{pic.name}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
              )}
            </Box>
          ) : (
            <Typography>Tidak ada data.</Typography>
          )}
        </DialogContent>

        <DialogActions>
          {selectedEvent?.attachment && (
            <Button
              variant="outlined"
              color="primary"
              size="small"
              startIcon={<DownloadIcon />}
              onClick={handleDownload}
              sx={{ textTransform: "none", fontWeight: 500 }}
            >
              Download
            </Button>
          )}

          {role === "SQI" && (
            <FormControl size="small" sx={{ minWidth: 120, mr: 2 }}>
              <InputLabel id="status-label">Status</InputLabel>
              <Select
                labelId="status-label"
                value={selectedStatus}
                onChange={handleStatusChange}
                label="Status"
              >
                {statuses.map((status) => (
                  <MenuItem key={status} value={status}>
                    {status === "null" ? "-" : status}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          )}
          <Button onClick={() => setOpenDialog(false)} color="primary">
            Tutup
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default DeploymentCalendarPage;
