import React, { useState, useEffect } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  TextField,
  Button,
  CircularProgress,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";

const KnowledgeCenter = () => {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);
  const [problem, setProblem] = useState("");
  const [solution, setSolution] = useState("");

  const fetchData = async (searchTerm = "") => {
    setLoading(true);
    try {
      const res = await api.get(`/knowledge-center?search=${searchTerm}`);
      setData(res.data.data || []);
    } catch (err) {
      console.error("Gagal mengambil data:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      fetchData(search);
    }, 500);
    return () => clearTimeout(delayDebounce);
  }, [search]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!problem || !solution) return;
    try {
      await api.post("/knowledge-center", { problem, solution });
      setProblem("");
      setSolution("");
      fetchData();
    } catch (err) {
      console.error("Gagal tambah data:", err);
    }
  };

  return (
    <Box p={4}>
      <Typography variant="h5" fontWeight="bold" mb={3}>
        üìò Knowledge Center
      </Typography>

      {/* üîç Live Search */}
      <TextField
        label="Cari Problem..."
        variant="outlined"
        fullWidth
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        sx={{ mb: 3 }}
      />

      {/* ‚ûï Form Tambah */}
      <Box
        component="form"
        onSubmit={handleSubmit}
        sx={{ mb: 4, display: "flex", gap: 2 }}
      >
        <TextField
          label="Problem"
          variant="outlined"
          fullWidth
          value={problem}
          onChange={(e) => setProblem(e.target.value)}
        />
        <TextField
          label="Solution"
          variant="outlined"
          fullWidth
          value={solution}
          onChange={(e) => setSolution(e.target.value)}
        />
        <Button type="submit" variant="contained">
          Simpan
        </Button>
      </Box>

      {loading ? (
        <CircularProgress />
      ) : (
        data.map((item) => (
          <Accordion key={item.id} sx={{ mb: 2 }}>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}>
              <Typography fontWeight="bold">{item.problem}</Typography>
            </AccordionSummary>
            <AccordionDetails>
              <Typography>{item.solution}</Typography>
            </AccordionDetails>
          </Accordion>
        ))
      )}
    </Box>
  );
};

export default KnowledgeCenter;
