import React from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import TaskListPage from "./pages/TaskListPage";
import CreateTaskPage from "./pages/CreateTaskPage";
import LoginPage from "./pages/LoginPage";
import MainLayout from "./layout/MainLayout"; // Gunakan layout dengan sidebar

// Komponen pembungkus agar bisa akses lokasi (path)
function AppContent() {
  const location = useLocation();

  // Daftar halaman yang tidak perlu menampilkan sidebar
  const hideSidebarRoutes = ["/login"];

  // Cek apakah halaman saat ini butuh sidebar atau tidak
  const showSidebar = !hideSidebarRoutes.includes(location.pathname);

  return (
    <>
      {showSidebar ? (
        // Halaman yang punya sidebar
        <MainLayout>
          <Routes>
            <Route path="/" element={<TaskListPage />} />
            <Route path="/create" element={<CreateTaskPage />} />
          </Routes>
        </MainLayout>
      ) : (
        // Halaman login tanpa sidebar
        <Routes>
          <Route path="/login" element={<LoginPage />} />
        </Routes>
      )}
    </>
  );
}

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
