import React, { useState } from "react";
import { Container, TextField, Button, Typography, Box, Paper } from "@mui/material";
import { useNavigate } from "react-router-dom";
import api from "../services/api"; // gunakan instance axios dari api.js

const LoginPage = () => {
  const [form, setForm] = useState({
    username: "",
    password: "",
  });
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Handle input change
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // reset error message

    try {
      // ðŸ”¥ Panggil endpoint backend login
      const response = await api.post("/auth/login", form);

      // Ambil data dari response
      const { token, role, username } = response.data;

      // Simpan ke localStorage
      localStorage.setItem("token", token);
      localStorage.setItem("role", role);
      localStorage.setItem("username", username);

      // Redirect sesuai role
      if (role === "sqi") {
        navigate("/tasks"); // Halaman daftar task
      } else if (role === "developer") {
        navigate("/create"); // Halaman buat task baru
      } else {
        setError("Role tidak dikenali.");
      }
    } catch (err) {
      console.error("Login error:", err);
      if (err.response?.status === 401) {
        setError("Username atau password salah.");
      } else {
        setError("Terjadi kesalahan saat login.");
      }
    }
  };

  return (
    <Container 
      maxWidth="xs" 
      sx={{ 
        mt: 8, 
        background: 'linear-gradient(45deg, #6a11cb 0%, #2575fc 100%)', 
        height: '100vh',
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center'
      }}
    >
      <Paper 
        elevation={6} 
        sx={{ 
          width: '100%', 
          padding: 4, 
          borderRadius: 2, 
          boxShadow: 3 
        }}
      >
        <Typography variant="h5" gutterBottom align="center" sx={{ fontWeight: 'bold', color: '#2575fc' }}>
          Login
        </Typography>

        <Box component="form" onSubmit={handleSubmit}>
          <TextField
            fullWidth
            label="Username"
            name="username"
            value={form.username}
            onChange={handleChange}
            margin="normal"
            required
            variant="outlined"
            sx={{ borderRadius: 1 }}
          />

          <TextField
            fullWidth
            label="Password"
            name="password"
            type="password"
            value={form.password}
            onChange={handleChange}
            margin="normal"
            required
            variant="outlined"
            sx={{ borderRadius: 1 }}
          />

          {error && (
            <Typography variant="body2" color="error" sx={{ mt: 1 }}>
              {error}
            </Typography>
          )}

          <Button
            type="submit"
            variant="contained"
            color="primary"
            sx={{ mt: 2, width: '100%', padding: 1.5, fontSize: 16 }}
          >
            Login
          </Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default LoginPage;
