import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Paper,
  CircularProgress,
  Card,
  CardContent,
  Chip,
  Pagination,
  Grid,
} from "@mui/material";

const formatDate = (date) =>
  new Date(date).toLocaleDateString("id-ID", {
    weekday: "long",
    day: "2-digit",
    month: "short",
  });

const getWeekStart = (date) => {
  const d = new Date(date);
  const day = d.getDay();
  const diff = d.getDate() - day + (day === 0 ? -6 : 1); // start on Monday
  return new Date(d.setDate(diff));
};

const DeploymentBoardPage = () => {
  const [deploymentRequests, setDeploymentRequests] = useState([]);
  const [deploymentSupports, setDeploymentSupports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentWeek, setCurrentWeek] = useState(0); // pagination minggu

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const startDate = new Date().toISOString().split("T")[0];
        const endDate = new Date(new Date().setMonth(new Date().getMonth() + 1))
          .toISOString()
          .split("T")[0];

        const [reqRes, supRes] = await Promise.all([
          api.get("/deployment-requests", { params: { startDate, endDate } }),
          api.get("/deployment-supports", { params: { startDate, endDate } }),
        ]);

        setDeploymentRequests(reqRes.data?.data || []);
        setDeploymentSupports(supRes.data?.data || []);
      } catch (err) {
        console.error("‚ùå Gagal mengambil data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  // üîπ Gabungkan semua event
  const allEvents = [
    ...deploymentRequests.map((r) => ({
      ...r,
      type: "request",
      date: r.implementDate,
    })),
    ...deploymentSupports.map((s) => ({
      ...s,
      type: "support",
      date: s.implementDate,
    })),
  ];

  // üîπ Kelompokkan berdasarkan minggu dan hari
  const eventsByWeek = {};
  allEvents.forEach((event) => {
    const weekStart = getWeekStart(event.date).toISOString().split("T")[0];
    if (!eventsByWeek[weekStart]) eventsByWeek[weekStart] = {};
    const dateStr = new Date(event.date).toISOString().split("T")[0];
    if (!eventsByWeek[weekStart][dateStr])
      eventsByWeek[weekStart][dateStr] = [];
    eventsByWeek[weekStart][dateStr].push(event);
  });

  const weekKeys = Object.keys(eventsByWeek).sort();
  const currentWeekKey = weekKeys[currentWeek] || weekKeys[0];
  const weekDays = Array.from({ length: 7 }, (_, i) => {
    const d = new Date(currentWeekKey);
    d.setDate(d.getDate() + i);
    return d;
  });

  const riskColor = (event) => {
    if (event.type === "support") return "#90caf9";
    switch (event.riskImpact) {
      case "Low":
        return "#A5D6A7";
      case "Medium":
        return "#FFF59D";
      case "High":
        return "#EF9A9A";
      case "Major Release":
        return "#3a3a3a";
      default:
        return "#E0E0E0";
    }
  };

  return (
    <Box sx={{ px: 2, pb: 5 }}>
      <Typography
        variant="h5"
        fontWeight="bold"
        sx={{ color: "#1976d2", mb: 3, textAlign: "center" }}
      >
        Deployment Board (Per Minggu)
      </Typography>

      {/* üîπ Layout 2 baris: atas 4 hari, bawah 3 hari */}
      {[0, 4].map((startIdx, rowIndex) => (
        <Box
          key={rowIndex}
          sx={{
            display: "flex",
            justifyContent: "center",
            flexWrap: "wrap",
            gap: 2,
            mb: 3,
          }}
        >
          {weekDays.slice(startIdx, startIdx + (rowIndex === 0 ? 4 : 3)).map((day) => {
            const dateStr = day.toISOString().split("T")[0];
            const events = eventsByWeek[currentWeekKey]?.[dateStr] || [];
            const requests = events.filter((e) => e.type === "request");
            const supports = events.filter((e) => e.type === "support");

            return (
              <Paper
                key={dateStr}
                sx={{
                  p: 2,
                  width: 280,
                  flexShrink: 0,
                  backgroundColor: "#f9f9f9",
                  display: "flex",
    flexDirection: "column",
    alignItems: "center", // üîπ center horizontal
    textAlign: "center",  // üîπ teks di tengah
                }}
              >
                <Typography
                  variant="subtitle1"
                  fontWeight="bold"
                  sx={{ mb: 1, color: "#1976d2" }}
                >
                  {formatDate(day)}
                </Typography>

                {/* üîπ Pembagian kiri-kanan */}
              <Grid container spacing={1}>
                {/* üîπ Kolom kiri - Request */}
                <Grid
                  item
                  xs={6}
                  sx={{
                    pr: 1,
                    borderRight: "1px solid #ddd", // garis pemisah
                  }}
                >
                  <Typography
                    variant="caption"
                    sx={{ fontWeight: "bold", display: "block", mb: 0.5 }}
                  >
                    Request
                  </Typography>
                  {requests.length === 0 ? (
                    <Typography variant="caption" sx={{ opacity: 0.6 }}>
                      (Kosong)
                    </Typography>
                  ) : (
                    requests.map((event) => (
                      <Card
                        key={event.id}
                        sx={{
                          mb: 1,
                          backgroundColor: riskColor(event),
                          cursor: "pointer",
                        }}
                      >
                        <CardContent sx={{ p: 1 }}>
                          <Typography variant="body2" fontWeight="bold" noWrap>
                            {event.title}
                          </Typography>
                        </CardContent>
                      </Card>
                    ))
                  )}
                </Grid>

                {/* üîπ Kolom kanan - Support */}
                <Grid item xs={6} sx={{ pl: 1 }}>
                  <Typography
                    variant="caption"
                    sx={{ fontWeight: "bold", display: "block", mb: 0.5 }}
                  >
                    Support
                  </Typography>
                  {supports.length === 0 ? (
                    <Typography variant="caption" sx={{ opacity: 0.6 }}>
                      (Kosong)
                    </Typography>
                  ) : (
                    supports.map((event) => (
                      <Card
                        key={event.id}
                        sx={{
                          mb: 1,
                          backgroundColor: riskColor(event),
                          cursor: "pointer",
                        }}
                      >
                        <CardContent sx={{ p: 1 }}>
                          <Typography variant="body2" fontWeight="bold" noWrap>
                            {event.title}
                          </Typography>
                        </CardContent>
                      </Card>
                    ))
                  )}
                </Grid>
              </Grid>

              </Paper>
            );
          })}
        </Box>
      ))}

      {/* üîπ Pagination untuk pindah minggu */}
      <Box sx={{ display: "flex", justifyContent: "center", mt: 2 }}>
        <Pagination
          count={weekKeys.length}
          page={currentWeek + 1}
          onChange={(e, val) => setCurrentWeek(val - 1)}
          color="primary"
        />
      </Box>
    </Box>
  );
};

export default DeploymentBoardPage;
