import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Paper,
  CircularProgress,
  Card,
  CardContent,
  Pagination,
  Grid,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
} from "@mui/material";
import DownloadIcon from "@mui/icons-material/Download";

// üîπ Format tanggal tampilan (Senin, 11 Nov)
const formatDate = (date) =>
  new Date(date).toLocaleDateString("id-ID", {
    weekday: "long",
    day: "2-digit",
    month: "short",
  });

// üîπ Hitung awal minggu (Senin)
const getWeekStart = (date) => {
  const d = new Date(date);
  const day = d.getDay();
  const diff = d.getDate() - day + (day === 0 ? -6 : 1);
  return new Date(d.setDate(diff));
};

// üîπ Tambah hari
const addDays = (date, days) => {
  const d = new Date(date);
  d.setDate(d.getDate() + days);
  return d;
};

// üîπ Format YYYY-MM-DD
const formatISO = (date) => date.toISOString().split("T")[0];

const DeploymentBoardPage = () => {
  const [eventsByWeek, setEventsByWeek] = useState({}); // { weekStart: { dateStr: [events] } }
  const [loading, setLoading] = useState(true);
  const [weekKeys, setWeekKeys] = useState([]); // semua minggu yang ada
  const [currentWeek, setCurrentWeek] = useState(0);
  const [loadedWeeks, setLoadedWeeks] = useState({}); // menyimpan minggu yang sudah load

  const [openDialog, setOpenDialog] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [statuses] = useState(["null", "success", "redeploy", "cancel"]);
  const [selectedStatus, setSelectedStatus] = useState("");
  const [sqiPics, setSqiPics] = useState([]);
  const [selectedPic, setSelectedPic] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);
  const role = "SQI";

  // üîπ Fetch data SQI PIC
  const fetchSqiPics = async () => {
    try {
      const res = await api.get("/sqi-pics");
      setSqiPics(res.data?.data || []);
    } catch (err) {
      console.error("‚ùå Gagal mengambil data SQI PIC:", err);
    }
  };

  // üîπ Fetch data kalender per minggu
  const fetchCalendarWeek = async (weekStartDate) => {
    const weekStart = getWeekStart(weekStartDate);
    const weekEnd = addDays(weekStart, 6);
    const weekKey = formatISO(weekStart);

    if (loadedWeeks[weekKey]) return; // sudah pernah load

    setLoading(true);
    try {
      const res = await api.get("/calendar", {
        params: {
          weekStart: formatISO(weekStart),
          weekEnd: formatISO(weekEnd),
        },
      });

      const rawData = Array.isArray(res.data.data) ? res.data.data : [];
      const weekEvents = {};

      rawData.forEach((item) => {
        const date = new Date(item.implementDate);
        if (!isNaN(date)) {
          const dateStr = formatISO(date);
          if (!weekEvents[dateStr]) weekEvents[dateStr] = [];
          weekEvents[dateStr].push(item);
        }
      });

      setEventsByWeek((prev) => ({ ...prev, [weekKey]: weekEvents }));
      setLoadedWeeks((prev) => ({ ...prev, [weekKey]: true }));

      // update weekKeys jika belum ada
      setWeekKeys((prevKeys) => {
        if (!prevKeys.includes(weekKey)) return [...prevKeys, weekKey].sort();
        return prevKeys;
      });
    } catch (err) {
      console.error("‚ùå Gagal mengambil data minggu:", err);
    } finally {
      setLoading(false);
    }
  };

  // üîπ Load minggu pertama saat mount
  useEffect(() => {
    const currentWeekStart = getWeekStart(new Date());
    setCurrentWeek(0);
    fetchCalendarWeek(currentWeekStart);
    if (role === "SQI") fetchSqiPics();
  }, []);

  // üîπ Pagination antar minggu
  const handleWeekChange = (e, val) => {
    setCurrentWeek(val - 1);
    const weekKey = weekKeys[val - 1];
    if (weekKey) {
      fetchCalendarWeek(new Date(weekKey));
    }
  };

  // üîπ Klik Card ‚Üí buka dialog
  const handleCardClick = (event) => {
    setSelectedEvent(event);
    setSelectedStatus(event.status || "null");
    setSelectedPic(event.sqiPicId || "");
    setOpenDialog(true);
  };

  // üîπ Download attachment
  const handleDownload = async () => {
    if (!selectedEvent?.attachment) return;
    try {
      const filename = selectedEvent.attachment.split("/").pop();
      const response = await api.get(`/deployment-requests/download/${filename}`, { responseType: "blob" });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error("‚ùå Gagal mengunduh file:", err);
      alert("Gagal mengunduh file.");
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
        <CircularProgress />
      </Box>
    );
  }

  // üîπ Tampilkan hari di minggu saat ini
  const currentWeekKey = weekKeys[currentWeek];
  const weekDays = Array.from({ length: 7 }, (_, i) => addDays(new Date(currentWeekKey), i));
  const eventsToday = eventsByWeek[currentWeekKey] || {};

  const riskColor = (event) => {
    switch (event.riskImpact) {
      case "Low": return "#A5D6A7";
      case "Medium": return "#FFF59D";
      case "High": return "#EF9A9A";
      case "Major Release": return "#3a3a3a";
      default: return "#E0E0E0";
    }
  };

  return (
    <Box sx={{ px: 2, pb: 5 }}>
      <Typography variant="h5" fontWeight="bold" sx={{ color: "#1976d2", mb: 3, textAlign: "center" }}>
        Deployment Board (Per Minggu)
      </Typography>

      <Grid container spacing={2}>
        {weekDays.map((day) => {
          const dateStr = formatISO(day);
          const dayEvents = eventsToday[dateStr] || [];
          const requests = dayEvents.filter((e) => e.type === "request");
          const supports = dayEvents.filter((e) => e.type === "support");

          return (
            <Grid item xs={12} sm={6} md={3} key={dateStr}>
              <Paper sx={{ p: 2, backgroundColor: "#f9f9f9" }}>
                <Typography variant="subtitle1" fontWeight="bold" sx={{ mb: 1, color: "#1976d2", textAlign: "center" }}>
                  {formatDate(day)}
                </Typography>

                <Grid container spacing={1}>
                  <Grid item xs={6} sx={{ borderRight: "1px solid #ddd" }}>
                    <Typography variant="caption" fontWeight="bold" display="block" mb={0.5}>Request</Typography>
                    {requests.length === 0 ? (
                      <Typography variant="caption" sx={{ opacity: 0.6 }}>(Kosong)</Typography>
                    ) : (
                      requests.map((event) => (
                        <Card key={event.id} sx={{ mb: 1, backgroundColor: riskColor(event), cursor: "pointer" }} onClick={() => handleCardClick(event)}>
                          <CardContent sx={{ px: 1, py: 0, height: 48, display: "flex", alignItems: "center", justifyContent: "center" }}>
                            <Typography variant="body2" fontWeight="bold" noWrap sx={{ width: "100%" }}>{event.title}</Typography>
                          </CardContent>
                        </Card>
                      ))
                    )}
                  </Grid>

                  <Grid item xs={6}>
                    <Typography variant="caption" fontWeight="bold" display="block" mb={0.5}>Support</Typography>
                    {supports.length === 0 ? (
                      <Typography variant="caption" sx={{ opacity: 0.6 }}>(Kosong)</Typography>
                    ) : (
                      supports.map((event) => (
                        <Card key={event.id} sx={{ mb: 1, backgroundColor: riskColor(event), cursor: "pointer" }} onClick={() => handleCardClick(event)}>
                          <CardContent sx={{ px: 1, py: 0, height: 48, display: "flex", alignItems: "center", justifyContent: "center" }}>
                            <Typography variant="body2" fontWeight="bold" noWrap sx={{ width: "100%" }}>{event.title}</Typography>
                          </CardContent>
                        </Card>
                      ))
                    )}
                  </Grid>
                </Grid>
              </Paper>
            </Grid>
          );
        })}
      </Grid>

      {/* Pagination antar minggu */}
      <Box sx={{ display: "flex", justifyContent: "center", mt: 2 }}>
        <Pagination count={weekKeys.length} page={currentWeek + 1} onChange={handleWeekChange} color="primary" />
      </Box>
    </Box>
  );
};

export default DeploymentBoardPage;
