import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Paper,
  CircularProgress,
  Card,
  CardContent,
  Chip,
} from "@mui/material";
import dayjs from "dayjs";
import isoWeek from "dayjs/plugin/isoWeek";
dayjs.extend(isoWeek);

const DeploymentBoardPage = () => {
  const [deploymentRequests, setDeploymentRequests] = useState([]);
  const [deploymentSupports, setDeploymentSupports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [reqRes, supRes] = await Promise.all([
          api.get("/deployment-requests"),
          api.get("/deployment-supports"),
        ]);
        setDeploymentRequests(reqRes.data?.data || []);
        setDeploymentSupports(supRes.data?.data || []);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", mt: 5 }}>
        <CircularProgress />
      </Box>
    );
  }

  // Gabungkan semua event
  const allEvents = [
    ...deploymentRequests.map((r) => ({ ...r, type: "request", date: r.implementDate })),
    ...deploymentSupports.map((s) => ({ ...s, type: "support", date: s.implementDate })),
  ];

  // Kelompokkan per minggu (Monday start)
  const eventsByWeek = {};
  allEvents.forEach((event) => {
    const week = dayjs(event.date).startOf("isoWeek").format("YYYY-MM-DD");
    if (!eventsByWeek[week]) eventsByWeek[week] = [];
    eventsByWeek[week].push(event);
  });

  const riskColor = (event) => {
    if (event.type === "support") return "#90caf9"; // biru muda untuk support
    switch (event.riskImpact) {
      case "Low":
        return "#A5D6A7";
      case "Medium":
        return "#FFF59D";
      case "High":
        return "#EF9A9A";
      case "Major Release":
        return "#3a3a3aff";
      default:
        return "#E0E0E0";
    }
  };

  return (
    <Box sx={{ p: 3, display: "flex", gap: 2, overflowX: "auto" }}>
      {Object.keys(eventsByWeek)
        .sort()
        .map((weekStart) => {
          const weekEnd = dayjs(weekStart).add(6, "day").format("YYYY-MM-DD");
          return (
            <Paper key={weekStart} sx={{ minWidth: 250, p: 2, flexShrink: 0 }}>
              <Typography variant="h6" fontWeight="bold" sx={{ mb: 1 }}>
                Minggu {dayjs(weekStart).format("DD MMM")} - {dayjs(weekEnd).format("DD MMM")}
              </Typography>
              <Box sx={{ display: "flex", flexDirection: "column", gap: 1 }}>
                {eventsByWeek[weekStart]
                  .sort((a, b) => new Date(a.date) - new Date(b.date))
                  .map((event) => (
                    <Card key={`${event.type}-${event.id}`} sx={{ backgroundColor: riskColor(event) }}>
                      <CardContent sx={{ p: 1 }}>
                        <Typography variant="body2" fontWeight="bold">
                          {event.title}
                        </Typography>
                        <Typography variant="caption">
                          {dayjs(event.date).format("DD MMM YYYY")}
                        </Typography>
                        <Chip
                          label={event.type === "request" ? "Request" : "Support"}
                          size="small"
                          sx={{ ml: 1 }}
                        />
                      </CardContent>
                    </Card>
                  ))}
              </Box>
            </Paper>
          );
        })}
    </Box>
  );
};

export default DeploymentBoardPage;
