// src/components/Navbar.js
import React from "react";
import {
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Box,
  Typography,
} from "@mui/material";
import { AddCircleOutline, ListAlt, Logout } from "@mui/icons-material";
import { useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();

  // ðŸ”¹ Ambil data user dari localStorage
  const user = JSON.parse(localStorage.getItem("user"));
  const role = user?.role || "";

  // ðŸ”¹ Menu utama (dynamic sesuai role)
  const menuItems = [
    // Hanya tampil kalau bukan SQI
    ...(role !== "sqi"
      ? [{ text: "Create ", icon: <AddCircleOutline />, path: "/create" }]
      : []),
    { text: "Support Task List", icon: <ListAlt />, path: "/tasks" },
  ];

  // ðŸ”¹ Logout handler
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    localStorage.removeItem("username");
    localStorage.removeItem("user");
    navigate("/login");
  };


  return (
    <Drawer
      variant="permanent"
      sx={{
        "& .MuiDrawer-paper": {
          width: "auto",
          minWidth: "160px",
          boxSizing: "border-box",
          backgroundColor: "#1976d2",
          color: "white",
          borderRight: "none",
          px: 2,
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-between",
        },
      }}
    >
      {/* ðŸ”¹ Header */}
      <Box sx={{ p: 2, textAlign: "center" }}>
        <Typography variant="h6" fontWeight="bold">
          SQI Support
        </Typography>
        <Typography variant="body2" sx={{ opacity: 0.8 }}>
          {role ? role.toUpperCase() : "GUEST"}
        </Typography>
      </Box>

      {/* ðŸ”¹ Menu utama */}
      <List>
        {menuItems.map((item) => (
          <ListItem key={item.text} disablePadding>
            <ListItemButton onClick={() => navigate(item.path)}>
              <ListItemIcon sx={{ color: "white" }}>{item.icon}</ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>

      {/* ðŸ”¹ Logout */}
      <List>
        <ListItem disablePadding>
          <ListItemButton onClick={handleLogout}>
            <ListItemIcon sx={{ color: "white" }}>
              <Logout />
            </ListItemIcon>
            <ListItemText primary="Logout" />
          </ListItemButton>
        </ListItem>
      </List>
    </Drawer>
  );
};

export default Navbar;
