import React, { useState, useEffect } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  TextField,
  IconButton,
  Stack,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  CircularProgress,
  Pagination,
  InputAdornment,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import SearchIcon from "@mui/icons-material/Search";

const KnowledgeCenter = () => {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  // Dropdown aplikasi
  const [applications, setApplications] = useState([]);
  const [selectedApp, setSelectedApp] = useState("");

  // Modal Add
  const [problem, setProblem] = useState("");
  const [solution, setSolution] = useState("");
  const [appForNew, setAppForNew] = useState("");
  const [openAddModal, setOpenAddModal] = useState(false);

  // Modal Edit/Delete
  const [openEditModal, setOpenEditModal] = useState(false);
  const [editId, setEditId] = useState(null);
  const [editProblem, setEditProblem] = useState("");
  const [editSolution, setEditSolution] = useState("");

  // Confirm delete
  const [openConfirm, setOpenConfirm] = useState(false);
  const [deleteId, setDeleteId] = useState(null);

  // Pagination
  const [page, setPage] = useState(1);
  const itemsPerPage = 10;

  // Ambil daftar aplikasi dari backend
  const fetchApplications = async () => {
    try {
      const res = await api.get("/applications");
      setApplications(res.data.data || []);
    } catch (err) {
      console.error("Failed to load applications:", err);
    }
  };

  // Ambil semua knowledge dengan filter dan search
  const fetchData = async (searchTerm = "", appFilter = "") => {
    setLoading(true);
    try {
      const query = new URLSearchParams();
      if (searchTerm) query.append("search", searchTerm);
      if (appFilter) query.append("applicationId", appFilter); // 汨 disamakan dengan backend key

      const res = await api.get(`/knowledge-center?${query.toString()}`);
      setData(res.data.data || []);
    } catch (err) {
      console.error("Failed to fetch knowledge center data:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchApplications();
    fetchData();
  }, []);

  useEffect(() => {
    const delay = setTimeout(() => {
      fetchData(search, selectedApp);
      setPage(1);
    }, 400);
    return () => clearTimeout(delay);
  }, [search, selectedApp]);

  // Add new
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!problem || !solution || !appForNew) return;
    try {
      await api.post("/knowledge-center", {
        problem,
        solution,
        applicationId: appForNew, // 汨 backend menerima applicationId
      });
      setProblem("");
      setSolution("");
      setAppForNew("");
      setOpenAddModal(false);
      fetchData(search, selectedApp);
    } catch (err) {
      console.error("Add failed:", err);
    }
  };

  const handleOpenEditModal = (item) => {
    setEditId(item.id);
    setEditProblem(item.problem);
    setEditSolution(item.solution);
    setOpenEditModal(true);
  };

  const handleSaveEdit = async () => {
    if (!editId) return;
    try {
      await api.put(`/knowledge-center/${editId}`, {
