import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  CircularProgress,
  Paper,
  Divider,
  List,
  ListItem,
  ListItemText,
  Chip,
  Grid,
} from "@mui/material";

const DeploymentCalendarPage = () => {
  const [deploymentRequests, setDeploymentRequests] = useState([]);
  const [deploymentSupports, setDeploymentSupports] = useState([]);
  const [loading, setLoading] = useState(true);

  // ambil range seminggu ke depan
  const today = new Date();
  const startDate = today.toISOString().split("T")[0];
  const endDate = new Date(today.setDate(today.getDate() + 6))
    .toISOString()
    .split("T")[0];

  const fetchData = async () => {
    setLoading(true);
    try {
      const [reqRes, supRes] = await Promise.all([
        api.get("/deployment-requests", { params: { startDate, endDate } }),
        api.get("/deployment-supports", { params: { startDate, endDate } }),
      ]);
      setDeploymentRequests(reqRes.data?.data || []);
      setDeploymentSupports(supRes.data?.data || []);
    } catch (err) {
      console.error("❌ Gagal mengambil data kalender:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const getWeekDays = () => {
    const days = [];
    const current = new Date();
    for (let i = 0; i < 7; i++) {
      const date = new Date();
      date.setDate(current.getDate() + i);
      days.push({
        key: date.toISOString().split("T")[0],
        label: date.toLocaleDateString("id-ID", {
          weekday: "long",
          day: "numeric",
          month: "short",
        }),
      });
    }
    return days;
  };

  if (loading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
          marginLeft: "220px",
        }}
      >
        <CircularProgress size={50} />
      </Box>
    );
  }

  const weekDays = getWeekDays();

  return (
    <Box sx={{ display: "flex" }}>
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          marginLeft: "220px",
          minHeight: "100vh",
        }}
      >
        <Typography variant="h5" fontWeight="bold" sx={{ color: "#1976d2", mb: 3 }}>
          Deployment Calendar (Weekly View)
        </Typography>

        {weekDays.map((day) => {
          const requests = deploymentRequests.filter(
            (r) => r.implementDate === day.key
          );
          const supports = deploymentSupports.filter(
            (s) => s.implementDate === day.key
          );

          return (
            <Paper
              key={day.key}
              elevation={3}
              sx={{ mb: 3, p: 2, borderRadius: 2, backgroundColor: "#fafafa" }}
            >
              <Typography
                variant="subtitle1"
                fontWeight="bold"
                sx={{ mb: 1, color: "#1976d2" }}
              >
                {day.label}
              </Typography>
              <Divider sx={{ mb: 2 }} />

              <Grid container spacing={2}>
                {/* KIRI - Deployment Request */}
                <Grid item xs={12} md={6}>
                  <Typography
                    variant="body1"
                    fontWeight="bold"
                    sx={{ mb: 1, color: "#1565c0" }}
                  >
                    Deployment Requests
                  </Typography>

                  {requests.length > 0 ? (
                    <List dense>
                      {requests.map((r) => (
                        <ListItem
                          key={r.id}
                          sx={{
                            border: "1px solid #ddd",
                            borderRadius: 2,
                            mb: 1,
                            p: 1.5,
                            backgroundColor:
                              r.riskImpact === "Low"
                                ? "#E8F5E9"
                                : r.riskImpact === "Medium"
                                ? "#FFFDE7"
                                : r.riskImpact === "High"
                                ? "#FFEBEE"
                                : r.riskImpact === "Major Release"
                                ? "#212121"
                                : "#F5F5F5",
                          }}
                        >
                          <ListItemText
                            primary={r.title}
                            secondary={
                              <>
                                <Typography variant="body2">
                                  App: {r.application?.name || "Unknown"}
                                </Typography>
                                <Typography variant="body2">
                                  Risk: {r.riskImpact || "-"}
                                </Typography>
                              </>
                            }
                            sx={{
                              color:
                                r.riskImpact === "Major Release"
                                  ? "#fff"
                                  : "#000",
                            }}
                          />
                          {r.status && (
                            <Chip
                              label={r.status}
                              color={
                                r.status === "success"
                                  ? "success"
                                  : r.status === "cancel"
                                  ? "error"
                                  : "default"
                              }
                              size="small"
                              sx={{ ml: 1 }}
                            />
                          )}
                        </ListItem>
                      ))}
                    </List>
                  ) : (
                    <Typography variant="body2" color="textSecondary">
                      Tidak ada deployment request hari ini.
                    </Typography>
                  )}
                </Grid>

                {/* KANAN - Deployment Support */}
                <Grid item xs={12} md={6}>
                  <Typography
                    variant="body1"
                    fontWeight="bold"
                    sx={{ mb: 1, color: "#2e7d32" }}
                  >
                    Deployment Supports
                  </Typography>

                  {supports.length > 0 ? (
                    <List dense>
                      {supports.map((s) => (
                        <ListItem
                          key={s.id}
                          sx={{
                            border: "1px solid #ddd",
                            borderRadius: 2,
                            mb: 1,
                            p: 1.5,
                            backgroundColor: "#E8F5E9",
                          }}
                        >
                          <ListItemText
                            primary={s.title}
                            secondary={
                              <>
                                <Typography variant="body2">
                                  App: {s.application || "-"}
                                </Typography>
                                <Typography variant="body2">
                                  Impacted: {s.impactedApplication || "-"}
                                </Typography>
                                <Typography variant="body2">
                                  Risk: {s.riskImpact || "-"}
                                </Typography>
                              </>
                            }
                          />
                        </ListItem>
                      ))}
                    </List>
                  ) : (
                    <Typography variant="body2" color="textSecondary">
                      Tidak ada deployment support hari ini.
                    </Typography>
                  )}
                </Grid>
              </Grid>
            </Paper>
          );
        })}
      </Box>
    </Box>
  );
};

export default DeploymentCalendarPage;
