import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Paper,
  CircularProgress,
  Card,
  CardContent,
  Chip,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  MenuItem,
  Grid,
} from "@mui/material";
import CalendarMonthIcon from "@mui/icons-material/CalendarMonth";

const DeploymentBoardPage = () => {
  const [deployments, setDeployments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedDeployment, setSelectedDeployment] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [pic, setPic] = useState("");
  const [status, setStatus] = useState("");
  const [error, setError] = useState(null);

  const fetchData = async () => {
    setLoading(true);
    try {
      const res = await api.get("/deployments");
      setDeployments(res.data);
    } catch (err) {
      console.error(err);
      setError("Gagal memuat data deployment");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleOpenDialog = (deployment) => {
    setSelectedDeployment(deployment);
    setPic(deployment.pic || "");
    setStatus(deployment.status || "");
    setOpenDialog(true);
  };

  // üîπ hanya tombol Close / klik luar yang trigger fetchData
  const handleCloseDialog = async () => {
    setOpenDialog(false);
    await fetchData();
  };

  const handleAssignPic = async () => {
    if (!selectedDeployment) return;
    try {
      await api.put(`/deployments/${selectedDeployment.id}/assign-pic`, { pic });
      // ‚ùå tidak reload & tidak menutup popup
      setSelectedDeployment({ ...selectedDeployment, pic });
    } catch (err) {
      console.error(err);
      setError("Gagal mengubah PIC");
    }
  };

  const handleStatusChange = async () => {
    if (!selectedDeployment) return;
    try {
      await api.put(`/deployments/${selectedDeployment.id}/update-status`, {
        status,
      });
      // ‚ùå tidak reload & tidak menutup popup
      setSelectedDeployment({ ...selectedDeployment, status });
    } catch (err) {
      console.error(err);
      setError("Gagal mengubah status");
    }
  };

  if (loading)
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="80vh">
        <CircularProgress />
      </Box>
    );

  if (error)
    return (
      <Typography color="error" align="center">
        {error}
      </Typography>
    );

  return (
    <Box p={3}>
      <Typography variant="h5" fontWeight="bold" mb={2}>
        <CalendarMonthIcon sx={{ mr: 1, verticalAlign: "middle" }} />
        Deployment Board
      </Typography>

      <Grid container spacing={2}>
        {deployments.map((d) => (
          <Grid item xs={12} sm={6} md={4} key={d.id}>
            <Card
              sx={{ cursor: "pointer", transition: "0.3s", "&:hover": { boxShadow: 6 } }}
              onClick={() => handleOpenDialog(d)}
            >
              <CardContent>
                <Typography variant="h6" noWrap>
                  {d.title}
                </Typography>
                <Typography variant="body2" color="text.secondary" noWrap>
                  {d.description}
                </Typography>
                <Chip
                  label={d.status || "Unknown"}
                  color={
                    d.status === "Released"
                      ? "success"
                      : d.status === "In Progress"
                      ? "warning"
                      : "default"
                  }
                  sx={{ mt: 1 }}
                />
                <Typography variant="caption" display="block" mt={1}>
                  PIC: {d.pic || "-"}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* üîπ Popup Detail Deployment */}
      <Dialog open={openDialog} onClose={handleCloseDialog} fullWidth>
        <DialogTitle>Deployment Detail</DialogTitle>
        <DialogContent>
          {selectedDeployment && (
            <Box mt={1}>
              <Typography variant="h6">{selectedDeployment.title}</Typography>
              <Typography variant="body2" color="text.secondary" mb={2}>
                {selectedDeployment.description}
              </Typography>

              <TextField
                select
                label="Assign PIC"
                fullWidth
                value={pic}
                onChange={(e) => setPic(e.target.value)}
                sx={{ mb: 2 }}
              >
                <MenuItem value="John">John</MenuItem>
                <MenuItem value="Jane">Jane</MenuItem>
                <MenuItem value="Doe">Doe</MenuItem>
              </TextField>
              <Button onClick={handleAssignPic} variant="contained" color="primary" fullWidth>
                Assign PIC
              </Button>

              <TextField
                select
                label="Status"
                fullWidth
                value={status}
                onChange={(e) => setStatus(e.target.value)}
                sx={{ mt: 3, mb: 2 }}
              >
                <MenuItem value="In Progress">In Progress</MenuItem>
                <MenuItem value="Released">Released</MenuItem>
                <MenuItem value="Pending">Pending</MenuItem>
              </TextField>
              <Button onClick={handleStatusChange} variant="contained" color="secondary" fullWidth>
                Update Status
              </Button>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default DeploymentBoardPage;
