import React, {
    useState
} from "react";
import axios from "axios";
import {
    Container,
    TextField,
    Button,
    Typography,
    Box
} from "@mui/material";
import {
    useNavigate
} from "react-router-dom";

const LoginPage = () => {
    const [form, setForm] = useState({
        username: "",
        password: "",
    });

    const [error, setError] = useState("");
    const navigate = useNavigate();

    // Handle perubahan input form
    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post("http://localhost:4000/api/auth/login", form);

            // Simpan token JWT di localStorage
            localStorage.setItem("token", response.data.token);

            // Dapatkan informasi role dari token (decoded)
            const decoded = JSON.parse(atob(response.data.token.split('.')[1]));

            // Redirect ke halaman sesuai role
            if (decoded.role === "sqi") {
                navigate("/tasks"); // Redirect ke halaman Task List untuk SQI
            } else if (decoded.role === "developer") {
                navigate("/create"); // Redirect ke halaman Create Task untuk Developer
            }
        } catch (err) {
            setError("Username atau Password salah");
            console.error("Login error", err);
        }
    };


    return ( <
        Container maxWidth = "xs"
        sx = {
            {
                mt: 8
            }
        } >
        <
        Typography variant = "h5"
        gutterBottom >
        Login <
        /Typography>

        <
        Box component = "form"
        onSubmit = {
            handleSubmit
        } > {
            /* Username */ } <
        TextField fullWidth label = "Username"
        name = "username"
        value = {
            form.username
        }
        onChange = {
            handleChange
        }
        margin = "normal"
        required /
        >

        {
            /* Password */ } <
        TextField fullWidth label = "Password"
        name = "password"
        type = "password"
        value = {
            form.password
        }
        onChange = {
            handleChange
        }
        margin = "normal"
        required /
        >

        {
            /* Error Message */ } {
            error && ( <
                Typography variant = "body2"
                color = "error"
                sx = {
                    {
                        mt: 1
                    }
                } > {
                    error
                } <
                /Typography>
            )
        }

        {
            /* Login Button */ } <
        Button type = "submit"
        variant = "contained"
        color = "primary"
        sx = {
            {
                mt: 2
            }
        }
        fullWidth >
        Login <
        /Button> <
        /Box> <
        /Container>
    );
};

export default LoginPage;
