// src/layout/MainLayout.js
import React from "react";
import Navbar from "../components/Navbar";
import {
  Box,
  CssBaseline
} from "@mui/material";

const drawerWidth = 220; // sama seperti di Navbar.js

const MainLayout = ({
  children
}) => {
  return ( <
    Box sx = {
      {
        display: "flex",
        minHeight: "100vh"
      }
    } >
    <
    CssBaseline / >
    <
    Navbar / >
    <
    Box component = "main"
    sx = {
      {
        flexGrow: 1,
        p: 4,
        backgroundColor: "#f4f6f8",
        ml: `${drawerWidth}px`, // tetap perlu untuk offset sidebar
        // ❌ hapus flex dan justifyContent
      }
    } >
    {
      children
    } <
    /Box>

    <
    /Box>
  );
};

export default MainLayout;
