// src/pages/CreateTaskPage.js
import React from "react";
import MainLayout from "../layout/MainLayout";
import TaskForm from "../components/TaskForm";
import { Box } from "@mui/material"; // Impor Box dari Material UI

const CreateTaskPage = () => {
  return (
    <MainLayout>
      {/* Tambahkan Box sebagai wadah konten utama dengan background berwarna.
        Ini akan membantu Anda melihat batas area kerja di samping sidebar.
      */}
      <Box
        sx={{
          minHeight: "100vh", // Memastikan box setinggi viewport
          bgcolor: "#f0f2f5", // Warna abu-abu terang untuk background (Anda bisa ganti)
          p: 3, // Padding di sekitar konten
          display: 'flex', // Gunakan flexbox pada parent container
          justifyContent: 'center', // Pusatkan konten secara horizontal
          alignItems: 'flex-start', // Pusatkan konten di bagian atas (bukan di tengah vertikal)
        }}
      >
        {/*
          TaskForm.js sudah memiliki Container dengan mx: 'auto'
          yang seharusnya sudah membuatnya centering di dalam Box ini.
        */}
        <TaskForm />
      </Box>
    </MainLayout>
  );
};

export default CreateTaskPage;
