import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Paper,
  CircularProgress,
  Card,
  CardContent,
  Pagination,
  Grid,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  Divider 
} from "@mui/material";
import DownloadIcon from "@mui/icons-material/Download";

const riskOrder = {
  "Major Release": 1,
  "High": 2,
  "Medium": 3,
  "Low": 4,
};

// üîπ Format tanggal tampilan (Senin, 11 Nov)
const formatDate = (date) =>
  new Date(date).toLocaleDateString("id-ID", {
    weekday: "long",
    day: "2-digit",
    month: "short",
  });

// üîπ Hitung awal minggu (Senin)
const getWeekStart = (date) => {
  if (!date || isNaN(new Date(date))) return new Date();
  const d = new Date(date);
  const day = d.getDay();
  const diff = d.getDate() - day + (day === 0 ? -6 : 1);
  return new Date(d.setDate(diff));
};

const DeploymentBoardPage = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentWeek, setCurrentWeek] = useState(0);

  // Popup states
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [statuses] = useState(["null", "success", "redeploy", "cancel"]);
  const [selectedStatus, setSelectedStatus] = useState("");
  const [sqiPics, setSqiPics] = useState([]);
  const [selectedPic, setSelectedPic] = useState("");
  const [isUpdating, setIsUpdating] = useState(false);
  const user = JSON.parse(localStorage.getItem("user"));
  const role = user?.role?.toLowerCase() || "guest";


  // üîπ Fetch data dari endpoint /calendar dan /sqi-pics
  useEffect(() => {
    const fetchCalendar = async () => {
      setLoading(true);
      try {
        const res = await api.get("/calendar");
        const rawData = res.data?.data || [];

        console.log("üì¶ Data mentah dari /calendar:", rawData);

        const cleaned = rawData
        .map((item) => {
          const dateField = item.implementDate || item.supportDate || item.date;

          // üîπ Lewati item tanpa tanggal valid
          if (!dateField) return null;

          const parsedDate = new Date(dateField);
          if (isNaN(parsedDate)) return null;

          return {
            ...item,
            date: parsedDate,
          };
        })
        .filter((e) => e !== null);
      // üß© Jika hasilnya kosong, isi minimal 1 data dummy agar tidak error
        if (cleaned.length === 0) {
          cleaned.push({
            id: "no-data",
            title: "Tidak ada data minggu ini",
            type: "info",
            date: new Date(), // fallback aman
            riskImpact: "Low",
          });
        }

      console.log("‚úÖ Cleaned data:", cleaned);
      setEvents(cleaned);

      } catch (err) {
        console.error("‚ùå Gagal mengambil data calendar:", err);
      } finally {
        setLoading(false);
      }
    };

    const fetchSqiPics = async () => {
      try {
        const res = await api.get("/sqi-pics");
        setSqiPics(res.data?.data || []);
      } catch (err) {
        console.error("‚ùå Gagal mengambil data SQI PIC:", err);
      }
    };

    fetchCalendar();
    fetchSqiPics();
  }, []);

  // üîπ Klik Card ‚Üí buka dialog
  const handleCardClick = (event) => {
    setSelectedEvent(event);
    setSelectedStatus(event.status || "null");
    setSelectedPic(event.sqiPicId || "");
    setOpenDialog(true);
  };

  // üîπ Download attachment
  const handleDownload = async () => {
    try {
      if (!selectedEvent?.attachment) return;
      const filename = selectedEvent.attachment.split("/").pop();
      const response = await api.get(`/deployment-requests/download/${filename}`, {
        responseType: "blob",
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error("‚ùå Gagal mengunduh file:", err);
      alert("Gagal mengunduh file.");
    }
  };

  // üîπ Ganti status
  const handleStatusChange = async (event) => {
    if (isUpdating) return;
    setIsUpdating(true);
    const newStatus = event.target.value;
    setSelectedStatus(newStatus);

    try {
      await api.patch(`/deployment-requests/${selectedEvent.id}`, {
        status: newStatus === "null" ? null : newStatus,
      });
      // update local state: selectedEvent dan events
      setSelectedEvent((prev) => ({
        ...prev,
        status: newStatus === "null" ? null : newStatus,
      }));
      setEvents((prevEvents) =>
      prevEvents.map((ev) =>
        ev.id === selectedEvent.id
          ? { ...ev, status: newStatus === "null" ? null : newStatus }
          : ev
      )
    );
    } catch (err) {
      console.error("‚ùå Gagal memperbarui status:", err);
      alert("Gagal memperbarui status.");
    } finally {
      setIsUpdating(false);
    }
  };

  // üîπ Assign SQI PIC
  const handleAssignPic = async (event) => {
    if (isUpdating) return;
    setIsUpdating(true);
    const picId = event.target.value;
    setSelectedPic(picId);

    try {
      await api.patch(`/deployment-requests/${selectedEvent.id}`, {
        sqiPicId: picId === "" ? null : picId,
      });
      // update local state
      setSelectedEvent((prev) => ({
        ...prev,
        sqiPicId: picId === "" ? null : picId,
      }));
      setEvents((prevEvents) =>
        prevEvents.map((ev) =>
          ev.id === selectedEvent.id
            ? { ...ev, sqiPicId: picId === "" ? null : picId }
            : ev
        )
      );
      alert("PIC berhasil di-assign!");
    } catch (err) {
      console.error("‚ùå Gagal assign PIC:", err);
      alert("Gagal assign PIC.");
    } finally {
      setIsUpdating(false);
    }
  };

  if (loading)
    return (
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
        <CircularProgress />
      </Box>
    );

  // üîπ Kelompokkan berdasarkan minggu dan hari
  const eventsByWeek = {};
  events.forEach((event) => {
    if (!(event.date instanceof Date) || isNaN(event.date)) return; // skip invalid date

    const weekStart = getWeekStart(event.date);
    if (!(weekStart instanceof Date) || isNaN(weekStart)) return;

    const weekStartStr = weekStart.toISOString().split("T")[0];
    if (!eventsByWeek[weekStartStr]) eventsByWeek[weekStartStr] = {};

    const dateStr = event.date.toISOString().split("T")[0];
    if (!eventsByWeek[weekStartStr][dateStr])
      eventsByWeek[weekStartStr][dateStr] = [];

    eventsByWeek[weekStartStr][dateStr].push(event);
  });


  const weekKeys = Object.keys(eventsByWeek).sort();
  const currentWeekKey = weekKeys[currentWeek] || weekKeys[0];

  const weekDays = Array.from({ length: 7 }, (_, i) => {
    const d = new Date(currentWeekKey);
    d.setDate(d.getDate() + i);
    return d;
  });

  const riskColor = (event) => {
    if (event.status === "cancel") return "#BDBDBD"; // abu-abu untuk cancel

    switch (event.riskImpact) {
      case "Low":
        return "#A5D6A7";
      case "Medium":
        return "#FFF59D";
      case "High":
        return "#EF9A9A";
      case "Major Release":
        return "#000000"; // hitam
      default:
        return "#E0E0E0";
    }
  };



  return (
    <Box sx={{ px: 2, pb: 5 }}>
      <Typography variant="h5" fontWeight="bold" sx={{ color: "#1976d2", mb: 3, textAlign: "center" }}>
        Deployment Board
      </Typography>

      <Box
          sx={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "stretch",
            flexWrap: "nowrap",
            gap: 1,
          }}
        >
          {weekDays.map((day) => {
            const dateStr = day.toISOString().split("T")[0];
            const eventsToday = eventsByWeek[currentWeekKey]?.[dateStr] || [];
            const requests = eventsToday.filter((e) => e.type === "request");
            const supports = eventsToday.filter((e) => e.type === "support");

            return (
              <Paper
                key={dateStr}
                sx={{
                  flex: "1 1 calc(100% / 7)", // bagi rata 7 kolom
                  p: 1.2,
                  minHeight: 600, // üü¢ tambahkan tinggi minimal default
                  backgroundColor: "#f6f6f6ff",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                  minWidth: 0, // agar teks wrap dengan baik
                }}
              >
                <Typography
                  variant="subtitle2"
                  fontWeight="bold"
                  sx={{ mb: 0.5, color: "#1976d2", textAlign: "center" }}
                >
                  {formatDate(day)}
                </Typography>

                <Grid container direction="column" spacing={1}>
                  {/* Request Section */}
                  <Grid item xs={12}>
                    <Typography
                      variant="caption"
                      sx={{ fontWeight: "bold", display: "block", mb: 0.5, textAlign: "center" }}
                    >
                      Request
                    </Typography>
                    {requests.length === 0 ? (
                      <Typography variant="caption" sx={{ opacity: 0.6, textAlign: "center" }}>
                        (Kosong)
                      </Typography>
                    ) : (
                      requests
                        .sort((a, b) => {
                          // ü©∂ "cancel" selalu di bawah
                          if (a.status === "cancel" && b.status !== "cancel") return 1;
                          if (a.status !== "cancel" && b.status === "cancel") return -1;

                          // urutkan berdasarkan riskImpact
                          return (riskOrder[a.riskImpact] || 99) - (riskOrder[b.riskImpact] || 99);
                        })

                        .map((event) => (
                          <Card
                            key={event.id}
                            sx={{
                              mb: 0.5,
                              backgroundColor: riskColor(event),
                              cursor: "pointer",
                              minWidth: "140px", 
                              maxWidth: "140px", 
                              display: "flex",
                              alignItems: "center",
                              justifyContent: "center",
                              color:
                                event.riskImpact === "Major Release"
                                  ? "#ffffffff" // teks putih untuk major release
                                  : "#000000", // teks hitam default
                            }}
                            onClick={() => handleCardClick(event)}
                          >
                            <CardContent
                              sx={{
                                px: 0.5,
                                py: 0.5,
                                "&:last-child": { pb: 0.5 },
                                height: 36,
                                display: "flex",
                                alignItems: "center",
                                justifyContent: "center",
                                minWidth: "140px", 
                                maxWidth: "140px",
                                padding: "8px 10px",
                                boxSizing: "border-box",
                              }}
                            >
                              <Typography
                                variant="body2"
                                fontWeight="bold"
                                sx={{
                                  width: "100%",
                                  overflow: "hidden",
                                  textOverflow: "ellipsis", // ‚ùó tambahkan titik-titik jika terlalu panjang
                                  whiteSpace: "nowrap",     // ‚ùó tidak turun ke bawah
                                  textAlign: "center",
                                  lineHeight: 1.2,
                                  }}
                              >
                                {event.title}
                              </Typography>
                            </CardContent>
                          </Card>
                        ))
                    )}
                  </Grid>

                  <Divider sx={{ my: 1, borderColor: "#ccc" }} />

                  {/* Support Section */}
                  <Grid item xs={12}>
                    <Typography
                      variant="caption"
                      sx={{ fontWeight: "bold", display: "block", mb: 0.5, textAlign: "center" }}
                    >
                      Support
                    </Typography>
                    {supports.length === 0 ? (
                      <Typography variant="caption" sx={{ opacity: 0.6, textAlign: "center" }}>
                        (Kosong)
                      </Typography>
                    ) : (
                      supports
                        .sort((a, b) => (riskOrder[a.riskImpact] || 99) - (riskOrder[b.riskImpact] || 99))
                        .map((event) => (
                          <Card
                            key={event.id}
                            sx={{
                              mb: 0.5,
                              backgroundColor: riskColor(event),
                              cursor: "pointer",
                              minWidth: "140px", 
                              maxWidth: "140px", 
                              display: "flex",
                              alignItems: "center",
                              justifyContent: "center",
                              color:
                                event.riskImpact === "Major Release"
                                  ? "#ffffffff" // teks putih untuk major release
                                  : "#000000", // teks hitam default

                            }}
                            onClick={() => handleCardClick(event)}
                          >
                            <CardContent
                              sx={{
                                px: 0.5,
                                py: 0.5,
                                "&:last-child": { pb: 0.5 },
                                // height: 36,
                          
                              }}
                            >
                              <Typography
                                variant="body2"
                                fontWeight="bold"
                                sx={{
                                  width: "100%",
                                  overflow: "hidden",
                                  textOverflow: "ellipsis", // ‚ùó tambahkan titik-titik jika terlalu panjang
                                  whiteSpace: "nowrap",     // ‚ùó tidak turun ke bawah
                                  textAlign: "center",
                                  lineHeight: 1.2,
                                  }}
                              >
                                {event.title}
                              </Typography>
                            </CardContent>
                          </Card>
                        ))
                    )}
                  </Grid>
                </Grid>
              </Paper>
            );
          })}
        </Box>



      <Box sx={{ display: "flex", justifyContent: "center", mt: 2 }}>
        <Pagination
          count={weekKeys.length}
          page={currentWeek + 1}
          onChange={(e, val) => setCurrentWeek(val - 1)}
          color="primary"
        />
      </Box>

      <Dialog open={openDialog} onClose={() => setOpenDialog(false)} fullWidth>
        <DialogTitle>Detail Event</DialogTitle>
        <DialogContent dividers>
          {selectedEvent ? (
            <Box sx={{ display: "flex", flexDirection: "column", gap: 1 }}>
              <Typography>
                <strong>Release ID:</strong> {selectedEvent.releaseId}
              </Typography>
              <Typography>
                <strong>Judul:</strong> {selectedEvent.title}
              </Typography>
              {/* <Typography>
                <strong>Tipe:</strong> {selectedEvent.type}
              </Typography> */}
              <Typography>
                <strong>Tanggal:</strong>{" "}
                {selectedEvent.implementDate || selectedEvent.supportDate}
              </Typography>
              <Typography>
                <strong>Risk Impact:</strong> {selectedEvent.riskImpact || "-"}
              </Typography>

              {/* üîπ SQI bisa assign PIC */}
              {role === "sqi" && selectedEvent?.type === "request" && (
                <FormControl size="small" sx={{ mt: 1, width: "100%" }}>
                  <InputLabel id="sqi-pic-label">Assign SQI PIC</InputLabel>
                  <Select
                    labelId="sqi-pic-label"
                    value={selectedPic}
                    onChange={handleAssignPic}
                    label="Assign SQI PIC"
                  >
                    {sqiPics.map((pic) => (
                      <MenuItem key={pic.id} value={pic.id}>
                        {pic.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              )}

              {/* üîπ Developer hanya bisa lihat PIC */}
              {role === "developer" && (
                <Typography sx={{ mt: 1 }}>
                  <strong>PIC SQI:</strong>{" "}
                  {sqiPics.find((p) => p.id === selectedEvent.sqiPicId)?.name || "-"}
                </Typography>
              )}

              {/* üîπ SQI bisa ubah status */}
              {role === "sqi" && selectedEvent?.type === "request" && (
                <FormControl size="small" sx={{ mt: 2, width: "100%" }}>
                  <InputLabel id="status-label">Status</InputLabel>
                  <Select
                    labelId="status-label"
                    value={selectedStatus}
                    onChange={handleStatusChange}
                    label="Status"
                  >
                    {statuses.map((status) => (
                      <MenuItem key={status} value={status}>
                        {status === "null" ? "-" : status}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              )}

              {/* üîπ Developer hanya lihat status */}
              {role === "developer" && (
                <Typography sx={{ mt: 1 }}>
                  <strong>Status:</strong> {selectedEvent.status || "-"}
                </Typography>
              )}
            </Box>
          ) : (
            <Typography>Tidak ada data.</Typography>
          )}
        </DialogContent>

<DialogActions>
  {selectedEvent?.attachment && (
    <Button
      variant="outlined"
      color="primary"
      size="small"
      startIcon={<DownloadIcon />}
      onClick={handleDownload}
    >
      Download
    </Button>
  )}

  <Button onClick={() => setOpenDialog(false)} color="primary">
    Tutup
  </Button>
</DialogActions>
      </Dialog>
    </Box>
  );
};

export default DeploymentBoardPage;
