import React, { useEffect, useState } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Paper,
  CircularProgress,
  Card,
  CardContent,
  Grid,
  Pagination,
} from "@mui/material";

// ðŸ”¹ Format tanggal tampilan
const formatDate = (date) =>
  new Date(date).toLocaleDateString("id-ID", {
    weekday: "long",
    day: "2-digit",
    month: "short",
  });

// ðŸ”¹ Hitung awal minggu (Senin)
const getWeekStart = (date) => {
  const d = new Date(date);
  const day = d.getDay();
  const diff = d.getDate() - day + (day === 0 ? -6 : 1);
  return new Date(d.setDate(diff));
};

const DeploymentBoardPage = () => {
  const [requests, setRequests] = useState([]);
  const [supports, setSupports] = useState([]);
  const [loading, setLoading] = useState(true);

  // ðŸ”¹ Pagination state (diambil dari backend)
  const [requestPage, setRequestPage] = useState(1);
  const [supportPage, setSupportPage] = useState(1);
  const [requestTotal, setRequestTotal] = useState(1);
  const [supportTotal, setSupportTotal] = useState(1);

  // ðŸ”¹ Range default (1 bulan)
  const startDate = new Date().toISOString().split("T")[0];
  const endDate = new Date(new Date().setMonth(new Date().getMonth() + 1))
    .toISOString()
    .split("T")[0];

  // ==========================
  // ðŸ”¹ Fetch Deployment Request
  // ==========================
  const fetchRequests = async (page = 1) => {
    try {
      const res = await api.get("/deployment-requests", {
        params: { startDate, endDate, page, limit: 10 },
      });
      setRequests(res.data.data || []);
      setRequestTotal(res.data.totalPages || 1);
    } catch (err) {
      console.error("âŒ Error fetch requests:", err);
    }
  };

  // ==========================
  // ðŸ”¹ Fetch Deployment Support
  // ==========================
  const fetchSupports = async (page = 1) => {
    try {
      const res = await api.get("/deployment-supports", {
        params: { startDate, endDate, page, limit: 10 },
      });
      setSupports(res.data.data || []);
      setSupportTotal(res.data.totalPages || 1);
    } catch (err) {
      console.error("âŒ Error fetch supports:", err);
    }
  };

  // ðŸ”¹ Load awal
  useEffect(() => {
    const load = async () => {
      setLoading(true);
      await Promise.all([fetchRequests(requestPage), fetchSupports(supportPage)]);
      setLoading(false);
    };
    load();
  }, [requestPage, supportPage]);

  if (loading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  // ðŸ”¹ Gabungkan dan kelompokkan per minggu
  const allEvents = [
    ...requests.map((r) => ({ ...r, type: "request", date: r.implementDate })),
    ...supports.map((s) => ({ ...s, type: "support", date: s.implementDate })),
  ];

  const eventsByWeek = {};
  allEvents.forEach((event) => {
    const weekStart = getWeekStart(event.date).toISOString().split("T")[0];
    if (!eventsByWeek[weekStart]) eventsByWeek[weekStart] = {};
    const dateStr = new Date(event.date).toISOString().split("T")[0];
    if (!eventsByWeek[weekStart][dateStr]) eventsByWeek[weekStart][dateStr] = [];
    eventsByWeek[weekStart][dateStr].push(event);
  });

  const weekKeys = Object.keys(eventsByWeek).sort();
  const riskColor = (event) => {
    if (event.type === "support") return "#90caf9";
    switch (event.riskImpact) {
      case "Low":
        return "#A5D6A7";
      case "Medium":
        return "#FFF59D";
      case "High":
        return "#EF9A9A";
      case "Major Release":
        return "#3a3a3a";
      default:
        return "#E0E0E0";
    }
  };

  return (
    <Box sx={{ px: 2, pb: 5 }}>
      <Typography
        variant="h5"
        fontWeight="bold"
        sx={{ color: "#1976d2", mb: 3, textAlign: "center" }}
      >
        Deployment Board (Per Minggu)
      </Typography>

      {weekKeys.map((weekKey, weekIndex) => {
        const weekDays = Array.from({ length: 7 }, (_, i) => {
          const d = new Date(weekKey);
          d.setDate(d.getDate() + i);
          return d;
        });

        return (
          <Box key={weekIndex} sx={{ mb: 4 }}>
            {/* Header Minggu */}
            <Typography
              variant="subtitle1"
              fontWeight="bold"
              sx={{ mb: 2, textAlign: "center" }}
            >
              Minggu {weekIndex + 1}: {formatDate(weekDays[0])} -{" "}
              {formatDate(weekDays[6])}
            </Typography>

            {/* ðŸ”¹ Layout 2 baris: 4 hari + 3 hari */}
            {[0, 4].map((startIdx, rowIndex) => (
              <Box
                key={rowIndex}
                sx={{
                  display: "flex",
                  justifyContent: "center",
                  flexWrap: "wrap",
                  gap: 2,
                  mb: 3,
                }}
              >
                {weekDays
                  .slice(startIdx, startIdx + (rowIndex === 0 ? 4 : 3))
                  .map((day) => {
                    const dateStr = day.toISOString().split("T")[0];
                    const events = eventsByWeek[weekKey]?.[dateStr] || [];
                    const requests = events.filter((e) => e.type === "request");
                    const supports = events.filter((e) => e.type === "support");

                    return (
                      <Paper
                        key={dateStr}
                        sx={{
                          p: 2,
                          width: 280,
                          flexShrink: 0,
                          backgroundColor: "#f9f9f9",
                          display: "flex",
                          flexDirection: "column",
                          alignItems: "center",
                          textAlign: "center",
                        }}
                      >
                        <Typography
                          variant="subtitle1"
                          fontWeight="bold"
                          sx={{ mb: 1, color: "#1976d2" }}
                        >
                          {formatDate(day)}
                        </Typography>

                        <Grid container spacing={1}>
                          {/* Request Column */}
                          <Grid
                            item
                            xs={6}
                            sx={{
                              pr: 1,
                              borderRight: "1px solid #ddd",
                              display: "flex",
                              flexDirection: "column",
                              alignItems: "center",
                            }}
                          >
                            <Typography
                              variant="caption"
                              sx={{ fontWeight: "bold", mb: 0.5 }}
                            >
                              Request
                            </Typography>
                            {requests.length === 0 ? (
                              <Typography variant="caption" sx={{ opacity: 0.6 }}>
                                (Kosong)
                              </Typography>
                            ) : (
                              requests.map((event) => (
                                <Card
                                  key={event.id}
                                  sx={{
                                    mb: 1,
                                    backgroundColor: riskColor(event),
                                    cursor: "pointer",
                                  }}
                                >
                                  <CardContent
                                    sx={{
                                      px: 1,
                                      py: 0,
                                      "&:last-child": { pb: 0 },
                                      height: 48,
                                      display: "flex",
                                      alignItems: "center",
                                      justifyContent: "center",
                                      textAlign: "center",
                                      minHeight: 48,
                                    }}
                                  >
                                    <Typography
                                      variant="body2"
                                      fontWeight="bold"
                                      noWrap
                                      sx={{ width: "100%" }}
                                    >
                                      {event.title}
                                    </Typography>
                                  </CardContent>
                                </Card>
                              ))
                            )}
                          </Grid>

                          {/* Support Column */}
                          <Grid
                            item
                            xs={6}
                            sx={{
                              pl: 1,
                              display: "flex",
                              flexDirection: "column",
                              alignItems: "center",
                            }}
                          >
                            <Typography
                              variant="caption"
                              sx={{ fontWeight: "bold", mb: 0.5 }}
                            >
                              Support
                            </Typography>
                            {supports.length === 0 ? (
                              <Typography variant="caption" sx={{ opacity: 0.6 }}>
                                (Kosong)
                              </Typography>
                            ) : (
                              supports.map((event) => (
                                <Card
                                  key={event.id}
                                  sx={{
                                    mb: 1,
                                    backgroundColor: riskColor(event),
                                    cursor: "pointer",
                                  }}
                                >
                                  <CardContent
                                    sx={{
                                      px: 1,
                                      py: 0,
                                      "&:last-child": { pb: 0 },
                                      height: 48,
                                      display: "flex",
                                      alignItems: "center",
                                      justifyContent: "center",
                                      textAlign: "center",
                                      minHeight: 48,
                                    }}
                                  >
                                    <Typography
                                      variant="body2"
                                      fontWeight="bold"
                                      noWrap
                                      sx={{ width: "100%" }}
                                    >
                                      {event.title}
                                    </Typography>
                                  </CardContent>
                                </Card>
                              ))
                            )}
                          </Grid>
                        </Grid>
                      </Paper>
                    );
                  })}
              </Box>
            ))}
          </Box>
        );
      })}

      {/* ðŸ”¹ Pagination Request & Support */}
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          gap: 5,
          mt: 3,
          flexWrap: "wrap",
        }}
      >
        <Box textAlign="center">
          <Typography variant="subtitle2">Request Pages</Typography>
          <Pagination
            count={requestTotal}
            page={requestPage}
            onChange={(e, val) => setRequestPage(val)}
            color="primary"
          />
        </Box>
        <Box textAlign="center">
          <Typography variant="subtitle2">Support Pages</Typography>
          <Pagination
            count={supportTotal}
            page={supportPage}
            onChange={(e, val) => setSupportPage(val)}
            color="primary"
          />
        </Box>
      </Box>
    </Box>
  );
};

export default DeploymentBoardPage;
