    // ... (state dan functions)

    return (
        // HAPUS Container, ganti dengan Box atau langsung Paper dengan styling
        <Paper
            elevation={3}
            sx={{
                p: 4,
                borderRadius: 3,
                width: '100%', // Ambil lebar penuh dari slot yang tersedia
                maxWidth: 600, // Batasi lebar maksimum form Anda
                mx: 'auto', // Centering horizontal yang andal
                mt: 5, // Margin atas seperti sebelumnya
            }}
        >
            {/* Sisa konten form seperti sebelumnya */}
            <Typography variant="h5" gutterBottom>
                Tambah Task Baru
            </Typography>
            <Box component="form" onSubmit={handleSubmit}>
                {/* ... fields ... */}
            </Box>
        </Paper>
    );
};

export default TaskForm;
