import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Paper,
  TextField,
  Button,
  Box,
} from '@mui/material';

function App() {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({
    title: '',
    description: '',
    status: 'pending'
  });

  // ðŸ”¹ Ambil data task dari backend
  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/tasks');
      setTasks(res.data);
    } catch (err) {
      console.error('Error fetching tasks:', err);
    }
  };

  // ðŸ”¹ Ambil data saat pertama kali halaman dibuka
  useEffect(() => {
    fetchTasks();
  }, []);

  // ðŸ”¹ Handle perubahan input form
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // ðŸ”¹ Kirim data baru ke backend
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:4000/api/tasks', form);
      setForm({ title: '', description: '', status: 'pending' });
      fetchTasks(); // refresh data
    } catch (err) {
      console.error('Error creating task:', err);
    }
  };

  return (
    <Container sx={{ mt: 5 }}>
      <Typography variant="h4" gutterBottom>
        SQI Support Task List
      </Typography>

      {/* ðŸ”¹ Form Tambah Task */}
      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="h6" gutterBottom>
          Tambah Task Baru
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', gap: 2 }}>
          <TextField
            label="Judul"
            name="title"
            value={form.title}
            onChange={handleChange}
            required
            fullWidth
          />
          <TextField
            label="Deskripsi"
            name="description"
            value={form.description}
            onChange={handleChange}
            required
            fullWidth
          />
          <Button type="submit" variant="contained" color="primary">
            Tambah
          </Button>
        </Box>
      </Paper>

      {/* ðŸ”¹ Tabel Daftar Task */}
      <Paper>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Judul</TableCell>
              <TableCell>Deskripsi</TableCell>
              <TableCell>Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {tasks.map((task) => (
              <TableRow key={task.id}>
                <TableCell>{task.id}</TableCell>
                <TableCell>{task.title}</TableCell>
                <TableCell>{task.description}</TableCell>
                <TableCell>{task.status}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
}

export default App;
