// src/pages/CreateTaskPage.js
import React from "react";
import MainLayout from "../layout/MainLayout";
import TaskForm from "../components/TaskForm";
import { Box } from "@mui/material"; // Impor Box dari Material UI

const CreateTaskPage = () => {
  return (
    <MainLayout>
      {/* Box ini sekarang hanya berfungsi sebagai visualisasi latar belakang
          dan wadah centering. Padding (p: 3) dihapus karena sudah ada padding
          dari MainLayout, menghindari padding ganda.
      */}
      <Box
        sx={{
          width: '100%', // Memastikan Box mengambil seluruh lebar yang dialokasikan
          minHeight: "100vh", // Memastikan box setinggi viewport
          bgcolor: "#e9ecef", // Warna abu-abu netral untuk background
          // p: 3, <-- Dihapus untuk menghindari padding ganda dengan MainLayout
          display: 'flex', // Gunakan flexbox pada parent container
          justifyContent: 'center', // Pusatkan konten secara horizontal
          alignItems: 'flex-start', // Pusatkan konten di bagian atas
        }}
      >
        {/*
          TaskForm.js (yang sudah memiliki Container dengan maxWidth='sm' dan mx: 'auto')
          akan center di dalam Box ini.
        */}
        <TaskForm />
      </Box>
    </MainLayout>
  );
};

export default CreateTaskPage;
