import React, { useState, useEffect } from "react";
import api from "../services/api";
import {
  Box,
  Typography,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  TextField,
  IconButton,
  Stack,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  CircularProgress,
  Pagination,
  InputAdornment,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import SaveIcon from "@mui/icons-material/Save";
import CloseIcon from "@mui/icons-material/Close";
import SearchIcon from "@mui/icons-material/Search";

const KnowledgeCenter = () => {
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  const [problem, setProblem] = useState("");
  const [solution, setSolution] = useState("");
  const [openAddModal, setOpenAddModal] = useState(false);

  const [editId, setEditId] = useState(null);
  const [editProblem, setEditProblem] = useState("");
  const [editSolution, setEditSolution] = useState("");

  const [deleteId, setDeleteId] = useState(null);
  const [openConfirm, setOpenConfirm] = useState(false);

  const [page, setPage] = useState(1);
  const itemsPerPage = 10;

  // Fetch data dari API
  const fetchData = async (searchTerm = "") => {
    setLoading(true);
    try {
      const res = await api.get(`/knowledge-center?search=${searchTerm}`);
      setData(res.data.data || []);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Live search dengan debounce
  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      fetchData(search);
      setPage(1);
    }, 300);
    return () => clearTimeout(delayDebounce);
  }, [search]);

  // Tambah data baru
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!problem || !solution) return;
    await api.post("/knowledge-center", { problem, solution });
    setProblem("");
    setSolution("");
    setOpenAddModal(false);
    fetchData(search);
  };

  // Edit
  const handleEdit = (item) => {
    setEditId(item.id);
    setEditProblem(item.problem);
    setEditSolution(item.solution);
  };
  const handleCancelEdit = () => {
    setEditId(null);
    setEditProblem("");
    setEditSolution("");
  };
  const handleSaveEdit = async (id) => {
    await api.put(`/knowledge-center/${id}`, {
      problem: editProblem,
      solution: editSolution,
    });
    setEditId(null);
    fetchData(search);
  };

  // Delete
  const confirmDelete = (id) => {
    setDeleteId(id);
    setOpenConfirm(true);
  };
  const handleDelete = async () => {
    await api.delete(`/knowledge-center/${deleteId}`);
    setOpenConfirm(false);
    fetchData(search);
  };

  // Pagination
  const handlePageChange = (event, value) => setPage(value);
  const startIndex = (page - 1) * itemsPerPage;
  const paginatedData = data.slice(startIndex, startIndex + itemsPerPage);
  const totalPages = Math.ceil(data.length / itemsPerPage);

  return (
    <Box>
      {/* HEADER */}
      <Box
        sx={{
          backgroundColor: "#1976d2",
          color: "white",
          py: 8,
          textAlign: "center",
          borderBottomLeftRadius: 24,
          borderBottomRightRadius: 24,
        }}
      >
        <Typography variant="h4" fontWeight="bold" mb={1}>
          Yok Tanya
        </Typography>
        <Typography variant="body1" mb={3}>
          Find answers to questions and solutions to problems
        </Typography>
        <TextField
          placeholder="Type keywords here..."
          variant="outlined"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          sx={{ width: "50%", backgroundColor: "white", borderRadius: 2 }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon />
              </InputAdornment>
            ),
            endAdornment: loading && <CircularProgress size={20} />,
          }}
        />
      </Box>

      {/* CONTENT */}
      <Box
        sx={{
          maxWidth: 800,
          mx: "auto",
          mt: -6,
          mb: 4,
          p: 4,
          bgcolor: "white",
          borderRadius: 3,
          boxShadow: 3,
        }}
      >
        <Typography variant="h5" fontWeight="bold" mb={3}>
          Add New
        </Typography>

        {/* Tombol Add New */}
        <Box sx={{ mb: 4 }}>
          <Button
            variant="contained"
            sx={{ borderRadius: 3, backgroundColor: "#1976d2" }}
            onClick={() => setOpenAddModal(true)}
          >
            Add New
          </Button>

          {/* Modal Add New */}
          <Dialog
            open={openAddModal}
            onClose={() => setOpenAddModal(false)}
            fullWidth
            maxWidth="sm"
          >
            <DialogTitle>Add New Problem & Solution</DialogTitle>
            <DialogContent>
              <Stack spacing={2} mt={1}>
                <TextField
                  placeholder="Problem"
                  variant="outlined"
                  fullWidth
                  value={problem}
                  onChange={(e) => setProblem(e.target.value)}
                />
                <TextField
                  placeholder="Solution"
                  variant="outlined"
                  fullWidth
                  value={solution}
                  onChange={(e) => setSolution(e.target.value)}
                />
              </Stack>
            </DialogContent>
            <DialogActions>
              <Button onClick={() => setOpenAddModal(false)}>Cancel</Button>
              <Button
                variant="contained"
                sx={{ backgroundColor: "#1976d2" }}
                onClick={handleSubmit}
              >
                Add
              </Button>
            </DialogActions>
          </Dialog>
        </Box>

        {loading ? (
          <Box display="flex" justifyContent="center" py={4}>
            <CircularProgress />
          </Box>
        ) : paginatedData.length === 0 ? (
          <Typography color="text.secondary" align="center">
            No FAQs found.
          </Typography>
        ) : (
          <>
            {paginatedData.map((item) => (
              <Accordion
                key={item.id}
                sx={{
                  mb: 2,
                  borderRadius: 2,
                  "&.Mui-expanded": { mb: 2 },
                  boxShadow: 1,
                }}
              >
                <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                  {editId === item.id ? (
                    <TextField
                      fullWidth
                      variant="standard"
                      value={editProblem}
                      onChange={(e) => setEditProblem(e.target.value)}
                    />
                  ) : (
                    <Typography fontWeight="bold">{item.problem}</Typography>
                  )}
                </AccordionSummary>
                <AccordionDetails>
                  {editId === item.id ? (
                    <>
                      <TextField
                        fullWidth
                        multiline
                        minRows={2}
                        variant="standard"
                        value={editSolution}
                        onChange={(e) => setEditSolution(e.target.value)}
                        sx={{ mb: 2 }}
                      />
                      <Stack direction="row" spacing={1}>
                        <IconButton onClick={() => handleSaveEdit(item.id)}>
                          <SaveIcon color="success" />
                        </IconButton>
                        <IconButton onClick={handleCancelEdit}>
                          <CloseIcon color="error" />
                        </IconButton>
                      </Stack>
                    </>
                  ) : (
                    <>
                      <Typography sx={{ mb: 2 }}>{item.solution}</Typography>
                      <Stack direction="row" spacing={1}>
                        <IconButton onClick={() => handleEdit(item)}>
                          <EditIcon color="primary" />
                        </IconButton>
                        <IconButton onClick={() => confirmDelete(item.id)}>
                          <DeleteIcon color="error" />
                        </IconButton>
                      </Stack>
                    </>
                  )}
                </AccordionDetails>
              </Accordion>
            ))}

            {/* Pagination */}
            <Box display="flex" justifyContent="center" mt={3}>
              <Pagination
                count={totalPages}
                page={page}
                onChange={handlePageChange}
                color="primary"
              />
            </Box>
          </>
        )}
      </Box>

      {/* Delete Dialog */}
      <Dialog open={openConfirm} onClose={() => setOpenConfirm(false)}>
        <DialogTitle>Delete FAQ</DialogTitle>
        <DialogContent>
          <Typography>Are you sure you want to delete this FAQ?</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenConfirm(false)}>Cancel</Button>
          <Button onClick={handleDelete} color="error" variant="contained">
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default KnowledgeCenter;
